import React from 'react';
import { Box, Typography, Divider } from '@mui/material';
import { useLanguage } from '../../context/LanguageContext';

/**
 * SampleImagePanel Component
 * Displays sample image information for printing examples
 */
const SampleImagePanel = () => {
  const { language } = useLanguage();

  // This would be the real image in a production environment
  // Using a placeholder gradient for visualization in the code
  const imageStyle = {
    width: '100%',
    height: '180px',
    borderRadius: '8px',
    background: 'linear-gradient(to right, #9733EE, #DA22FF)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
    fontWeight: 'bold',
    fontSize: '24px',
    marginBottom: '12px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
  };

  const captionText = language === 'ja' 
    ? 'このデータを使用して印刷しています'
    : 'This data is being used for printing';

  const titleText = language === 'ja' 
    ? 'この画像を印刷した場合'
    : 'When this image is printed';

  return (
    <Box>
      <Typography 
        variant="body1" 
        sx={{ 
          fontWeight: 600, 
          mb: 1.5,
          color: 'text.primary' 
        }}
      >
        {titleText}
      </Typography>

      <Box sx={imageStyle}>
        Lisa
      </Box>

      <Divider sx={{ my: 1.5 }} />

      <Typography 
        variant="body2" 
        sx={{ 
          fontWeight: 500, 
          color: 'text.secondary',
          textAlign: 'center',
          mt: 1
        }}
      >
        {captionText}
      </Typography>
    </Box>
  );
};

export default SampleImagePanel;
