import React from 'react';
import {
  Popover,
  Typography,
  Box,
  useMediaQuery,
  useTheme,
  Button,
  TextField,
  InputAdornment
} from '@mui/material';
import { useLanguage } from '../../../context/LanguageContext';

// Simple dialog component for mobile
const MobileDialog = ({ isOpen, onClose, title, children, initialInvestment, onInitialInvestmentChange }) => {
  if (!isOpen) return null;

  const handleInvestmentChange = (e) => {
    const value = e.target.value.replace(/,/g, '');
    if (value === '' || /^\d+$/.test(value)) {
      onInitialInvestmentChange(value === '' ? 0 : parseInt(value, 10));
    }
  };

  return (
    <div 
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        zIndex: 9999,
        backgroundColor: 'white',
        padding: '20px',
        display: 'flex',
        flexDirection: 'column',
        overflowY: 'auto'
      }}
    >
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        marginBottom: '20px',
        borderBottom: '1px solid #eee',
        paddingBottom: '10px'
      }}>
        <h2 style={{ margin: 0, fontSize: '20px' }}>{title}</h2>
        <button 
          onClick={onClose}
          style={{
            background: 'none',
            border: 'none',
            fontSize: '24px',
            cursor: 'pointer',
            padding: '5px 10px'
          }}
        >
          ✕
        </button>
      </div>

      {/* Sample Image */}
      <div style={{ marginBottom: '20px', textAlign: 'center' }}>
        <p style={{ 
          margin: '0 0 10px 0', 
          fontSize: '16px', 
          fontWeight: 'bold',
          color: '#333' 
        }}>
          サンプル印刷
        </p>
        <img 
          src="/assets/images/lisa.png" 
          alt="Sample Print"
          style={{
            maxWidth: '100%',
            height: 'auto',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
          }}
        />
        <p style={{ 
          margin: '10px 0 0 0', 
          fontSize: '14px', 
          color: '#666' 
        }}>
          全てのシミュレーションは、この画像を基に算出しています
        </p>
      </div>

      {/* Parameters Section */}
      <div style={{ marginBottom: '20px' }}>
        <h3 style={{ fontSize: '16px', marginTop: 0 }}>
          基本パラメータ
        </h3>

        {/* Initial Investment - Editable */}
        <div style={{ marginBottom: '16px' }}>
          <label style={{ 
            display: 'block', 
            fontWeight: 'bold', 
            marginBottom: '10px' 
          }}>
            初期投資額 (Initial Investment):
          </label>
          <input 
            type="text"
            value={initialInvestment.toLocaleString()}
            onChange={handleInvestmentChange}
            style={{
              width: '100%',
              padding: '10px',
              fontSize: '16px',
              border: '1px solid #ccc',
              borderRadius: '4px'
            }}
          />
        </div>

        {/* Printable Area - Static */}
        <div>
          <div style={{ 
            fontWeight: 'bold', 
            marginBottom: '6px' 
          }}>
            印刷領域 (Printable Area):
          </div>
          <div style={{ fontSize: '16px' }}>
            305mm × 458mm
          </div>
        </div>
      </div>

      <div style={{ marginTop: 'auto', paddingTop: '20px' }}>
        <button
          onClick={onClose}
          style={{
            width: '100%',
            padding: '12px',
            backgroundColor: '#2196f3',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            fontSize: '18px',
            fontWeight: 'bold',
            cursor: 'pointer',
            boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
            letterSpacing: '1px'
          }}
        >
          閉じる (Close)
        </button>
      </div>
    </div>
  );
};

/**
 * PrinterInfoPopover Component
 * Displays printer specifications in a popover when info icon is clicked
 */
const PrinterInfoPopover = ({ open, anchorEl, handleClose, initialInvestment, onInitialInvestmentChange }) => {
  const { t, language } = useLanguage();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const handleInvestmentChange = (e) => {
    const value = e.target.value.replace(/,/g, '');
    if (value === '' || /^\d+$/.test(value)) {
      onInitialInvestmentChange(value === '' ? 0 : parseInt(value, 10));
    }
  };

  // Use simple custom dialog for mobile
  if (isMobile) {
    return (
      <MobileDialog
        isOpen={open}
        onClose={handleClose}
        title={t('printerSpecifications')}
        initialInvestment={initialInvestment}
        onInitialInvestmentChange={onInitialInvestmentChange}
      />
    );
  }

  // Desktop version uses Material UI Popover
  return (
    <Popover
      open={open}
      anchorEl={anchorEl}
      onClose={handleClose}
      anchorOrigin={{
        vertical: 'bottom',
        horizontal: 'right',
      }}
      transformOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      PaperProps={{
        sx: { 
          width: 'auto', 
          minWidth: 450, 
          maxWidth: 500, 
          p: 3,
          borderRadius: 2,
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)'
        }
      }}
    >
      <Typography variant="h6" gutterBottom sx={{ fontWeight: 600, borderBottom: '1px solid rgba(0, 0, 0, 0.1)', pb: 1, mb: 2 }}>
        {t('printerSpecifications')}
      </Typography>

      {/* Sample Image for Desktop */}
      <Box sx={{ mb: 2, textAlign: 'center' }}>
        <Typography variant="body1" sx={{ mb: 1, fontWeight: 600 }}>
          {language === 'ja' ? 'サンプル印刷' : 'Sample Print'}
        </Typography>
        <img 
          src="/assets/images/lisa.png" 
          alt="Sample Print"
          style={{
            maxWidth: '100%',
            height: 'auto',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
          }}
        />
        <Typography variant="caption" display="block" sx={{ mt: 1, color: 'text.secondary' }}>
          {language === 'ja' ? '全てのシミュレーションは、この画像を基に算出しています' : 'Simulations are based on the above image'}
        </Typography>
      </Box>

      <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 600, color: 'text.primary', mb: 1 }}>
        {t('baseParameters')}
      </Typography>

      <Box sx={{ mb: 2.5 }}>
        <Box sx={{ 
          display: 'flex', 
          flexDirection: 'column',
          mb: 2
        }}>
          <Typography variant="body2" sx={{ fontWeight: 500, mb: 1 }}>
            {t('initialInvestment')}:
          </Typography>

          <TextField
            value={initialInvestment.toLocaleString()}
            onChange={handleInvestmentChange}
            variant="outlined"
            size="small"
            InputProps={{
              endAdornment: <InputAdornment position="end">{t('currency')}</InputAdornment>,
              sx: { fontWeight: 500 }
            }}
            sx={{ 
              '& .MuiOutlinedInput-root': {
                '& fieldset': {
                  borderColor: 'rgba(0, 0, 0, 0.15)',
                },
                '&:hover fieldset': {
                  borderColor: 'primary.main',
                },
                '&.Mui-focused fieldset': {
                  borderColor: 'primary.main',
                  borderWidth: 1,
                }
              }
            }}
          />
        </Box>

        <Box sx={{ 
          display: 'flex', 
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <Typography variant="body2" component="span" sx={{ fontWeight: 500 }}>
            {t('printableArea')}:
          </Typography>
          <Typography variant="body2" component="span" sx={{ fontWeight: 600 }}>
            305mm × 458mm
          </Typography>
        </Box>
      </Box>
    </Popover>
  );
};

export default PrinterInfoPopover;
