// MO Simulator 2/src/features/simulation/SimulationForm.js
// OR MO Simulator 2/src/components/InputPanel/InputPanel.js

import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Slider,
  Grid,
  InputAdornment,
  Card,
  CardContent,
  Tooltip,
  Button,
  Fade,
  useMediaQuery,
  styled
} from '@mui/material';
import { useTheme } from '@mui/material/styles';
import { useLanguage } from '../../context/LanguageContext';
// Correct the path based on your project structure
import PrinterInfoPopover from './components/PrinterInfoPopover';

// Using SVG icons directly instead of emojis to match the design
const DimensionsIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M5 5L9 9" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M19 5L15 9" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M5 19L9 15" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M19 19L15 15" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M5 5L3 3" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M19 5L21 3" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M5 19L3 21" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M19 19L21 21" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
  </svg>
);

const PriceIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="9" stroke="currentColor" strokeWidth="2"/>
    <path d="M12 7V17" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
    <path d="M8 12H16" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
  </svg>
);

const VolumeIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M3 17H7V10H3V17Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M10 17H14V3H10V17Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M17 17H21V6H17V17Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M3 21H21" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
  </svg>
);

const InfoIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/>
    <path d="M12 16V12" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
    <circle cx="12" cy="8" r="1" fill="currentColor"/>
  </svg>
);

// Custom slider with enhanced visibility
const VisibleSlider = styled(Slider)({
  color: '#2471CC',
  height: 8,
  padding: '13px 0',
  '& .MuiSlider-track': {
    height: 8,
    borderRadius: 4,
    backgroundColor: '#2471CC',
  },
  '& .MuiSlider-rail': {
    height: 8,
    borderRadius: 4,
    backgroundColor: '#d1d1d1',
  },
  '& .MuiSlider-thumb': {
    height: 24,
    width: 24,
    backgroundColor: '#fff',
    border: '2px solid #2471CC',
    boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
    '&:focus, &:hover, &.Mui-active': {
      boxShadow: '0 0 0 8px rgba(36, 113, 204, 0.2)',
    },
  },
});

/**
 * SimulationForm Component / InputPanel Component
 * Handles user inputs for the simulation with enhanced styling and improved aesthetics
 */
const SimulationForm = ({ inputs, onInputChange }) => { // Or InputPanel
  const theme = useTheme();
  const { t, language } = useLanguage(); // Now we get language from useLanguage
  const isMobile = useMediaQuery(theme.breakpoints.down('sm')); // For responsive styling
  const isExtraSmallMobile = useMediaQuery('(max-width:380px)');

  const [infoAnchorEl, setInfoAnchorEl] = useState(null);
  const infoPopoverOpen = Boolean(infoAnchorEl);

  const [focusedInput, setFocusedInput] = useState(null);
  const [editingDimension, setEditingDimension] = useState(null);
  const [tempDimensionValue, setTempDimensionValue] = useState("");

  // State to track input values during editing to avoid losing focus
  const [editingValues, setEditingValues] = useState({});

  const handleInfoClick = (event) => {
    setInfoAnchorEl(event.currentTarget);
  };

  const handleInfoClose = () => {
    setInfoAnchorEl(null);
  };

  // This function will be passed to PrinterInfoPopover
  // It calls the onInputChange from props, which ultimately updates the useSimulation hook
  const handleInitialInvestmentChange = (value) => {
    onInputChange('initialInvestment', value);
  };

  const handleNumericChange = (name, value, min, max) => {
    // During editing, just update the local state
    setEditingValues({
      ...editingValues,
      [name]: value
    });
  };

  const handleFocus = (name) => {
    setFocusedInput(name);
    // Initialize editing value
    setEditingValues({
      ...editingValues,
      [name]: inputs[name]
    });
  };

  const handleBlur = (name, min, max) => {
    setFocusedInput(null);

    // Now process the edited value and update the parent
    const rawValue = editingValues[name]?.toString().replace(/,/g, '') || '';

    if (rawValue === '') {
      onInputChange(name, '');
    } else {
      const numValue = parseFloat(rawValue);
      if (!isNaN(numValue)) {
        let finalValue = numValue;
        if (min !== undefined) finalValue = Math.max(min, finalValue);
        if (max !== undefined) finalValue = Math.min(max, finalValue);
        onInputChange(name, finalValue);
      } else {
        // If invalid, revert to previous value
        setEditingValues({
          ...editingValues,
          [name]: inputs[name]
        });
      }
    }
  };

  const handleStepperClick = (name, step, min, max) => {
    const currentValue = parseFloat(inputs[name]) || 0; // Ensure current value is a number
    let newValue = currentValue + step;

    if (min !== undefined) newValue = Math.max(min, newValue);
    if (max !== undefined) newValue = Math.min(max, newValue);

    onInputChange(name, newValue);
  };

  const startEditingDimension = (name, currentValue) => {
    setEditingDimension(name);
    setTempDimensionValue(currentValue.toString());
  };

  const handleDimensionChange = (e) => {
    setTempDimensionValue(e.target.value);
  };

  const finishEditingDimension = (name, min, max) => {
    const value = parseInt(tempDimensionValue, 10);
    if (!isNaN(value) && value >= min && value <= max) {
      onInputChange(name, value);
    } else {
      // Revert to original value if input is invalid
      setTempDimensionValue(inputs[name].toString());
    }
    setEditingDimension(null);
  };

  const handleDimensionKeyDown = (e, name, min, max) => {
    if (e.key === 'Enter') {
      finishEditingDimension(name, min, max);
      e.target.blur();
    } else if (e.key === 'Escape') {
      setTempDimensionValue(inputs[name].toString()); // Revert on escape
      setEditingDimension(null);
      e.target.blur();
    }
  };

  const getFocusBorderColor = (name) => {
    if (name === focusedInput) {
      return theme.palette.primary.main;
    }
    return 'transparent';
  };

  const SectionHeader = ({ icon, title }) => (
    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2.5 }}>
      <Box sx={{
        mr: 1.5,
        color: 'primary.main',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        {icon}
      </Box>
      <Typography variant="h6" fontWeight="600" color="text.primary" sx={{ fontSize: isMobile ? '1.1rem' : '1.25rem' }}>
        {title}
      </Typography>
    </Box>
  );

  const StepperInput = ({
    label,
    value,
    name,
    endAdornment,
    min,
    max,
    helperText,
    startAdornment,
    step = 1,
    fullWidth = true
  }) => {
    // Use editing value while focused, otherwise use the input value
    const displayValue = (focusedInput === name && editingValues[name] !== undefined) 
      ? editingValues[name] 
      : (value === '' ? '' : Number(value).toLocaleString(language === 'ja' ? 'ja-JP' : 'en-US'));

    return (
      <Box sx={{ mb: 2.5 }}>
        <Typography variant="subtitle2" gutterBottom sx={{ fontWeight: 500, color: 'text.secondary' }}>
          {label}
        </Typography>
        <Box sx={{ 
          display: 'flex', 
          alignItems: 'center',
          width: '100%' // Ensure the container takes full width 
        }}>
          <Button
            variant="outlined"
            onClick={() => handleStepperClick(name, -step, min, max)}
            sx={{
              minWidth: '44px',
              width: '44px', // Fixed width
              height: '44px',
              padding: 0,
              borderRadius: '8px 0 0 8px',
              borderRight: isMobile ? 1 : 0,
              fontSize: '1.2rem',
              fontWeight: 'bold',
              color: 'text.secondary',
              borderColor: 'rgba(0, 0, 0, 0.2)',
              backgroundColor: '#f5f5f5',
              flexShrink: 0, // Prevent shrinking
              '&:hover': {
                borderColor: 'primary.main',
                backgroundColor: 'rgba(0, 0, 0, 0.05)',
              }
            }}
          >
            −
          </Button>
          <TextField
            value={displayValue}
            onChange={(e) => handleNumericChange(name, e.target.value, min, max)}
            onFocus={() => handleFocus(name)}
            onBlur={() => handleBlur(name, min, max)}
            InputProps={{
              startAdornment: startAdornment && <InputAdornment position="start">{startAdornment}</InputAdornment>,
              endAdornment: endAdornment && <InputAdornment position="end">{endAdornment}</InputAdornment>,
            }}
            inputProps={{
              min,
              max,
              style: {
                textAlign: 'center',
                fontSize: isMobile ? '0.9rem' : '1rem',
                padding: '8px 0'
              }
            }}
            type="text" // Text to allow formatted numbers
            variant="outlined"
            size="medium"
            fullWidth
            sx={{
              flex: 1, // Take up remaining space
              '& .MuiOutlinedInput-root': {
                borderRadius: isMobile ? '0px' : 0,
                fontWeight: 500,
                backgroundColor: '#fff',
                height: '44px',
                transition: 'all 0.2s ease-in-out',
                '& .MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(0, 0, 0, 0.2)',
                  borderLeftWidth: isMobile ? 0 : 0,
                  borderRightWidth: isMobile ? 0 : 0,
                },
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  borderColor: 'primary.main',
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  borderColor: getFocusBorderColor(name),
                  borderWidth: 1,
                  borderLeftWidth: isMobile ? 0 : 0,
                  borderRightWidth: isMobile ? 0 : 0,
                }
              },
              '& input::-webkit-inner-spin-button, & input::-webkit-outer-spin-button': {
                WebkitAppearance: 'none',
                margin: 0
              },
              '& input[type=number]': {
                MozAppearance: 'textfield'
              }
            }}
          />
          <Button
            variant="outlined"
            onClick={() => handleStepperClick(name, step, min, max)}
            sx={{
              minWidth: '44px',
              width: '44px', // Fixed width
              height: '44px',
              padding: 0,
              borderRadius: '0 8px 8px 0',
              borderLeft: isMobile ? 1 : 0,
              fontSize: '1.2rem',
              fontWeight: 'bold',
              color: 'text.secondary',
              borderColor: 'rgba(0, 0, 0, 0.2)',
              backgroundColor: '#f5f5f5',
              flexShrink: 0, // Prevent shrinking
              '&:hover': {
                borderColor: 'primary.main',
                backgroundColor: 'rgba(0, 0, 0, 0.05)',
              }
            }}
          >
            +
          </Button>
        </Box>
        {helperText && (
          <Typography variant="caption" color="text.secondary">
            {helperText}
          </Typography>
        )}
      </Box>
    );
  };

  const DimensionInput = ({
    label,
    value,
    name,
    min,
    max,
    helperText,
    step = 5
  }) => (
    <div>
      <Typography variant="subtitle2" gutterBottom sx={{ fontWeight: 500, color: 'text.secondary' }}>
        {label} (mm)
      </Typography>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
        <Button
          variant="outlined"
          onClick={() => handleStepperClick(name, -step, min, max)}
          sx={{
            minWidth: isExtraSmallMobile ? '30px' : '40px',
            width: isExtraSmallMobile ? '30px' : '40px',
            height: isExtraSmallMobile ? '34px' : '40px',
            padding: 0,
            borderRadius: '8px',
            fontSize: '1.2rem',
            fontWeight: 'bold',
            color: 'text.secondary',
            borderColor: 'rgba(0, 0, 0, 0.2)',
            backgroundColor: '#f5f5f5',
            mr: 1,
            '&:hover': {
              borderColor: 'primary.main',
              backgroundColor: 'rgba(0, 0, 0, 0.05)',
            }
          }}
        >
          −
        </Button>
        {editingDimension === name ? (
          <TextField
            autoFocus
            value={tempDimensionValue}
            onChange={handleDimensionChange}
            onBlur={() => finishEditingDimension(name, min, max)}
            onKeyDown={(e) => handleDimensionKeyDown(e, name, min, max)}
            variant="outlined"
            size="small"
            type="number"
            inputProps={{
              min,
              max,
              style: { textAlign: 'center' }
            }}
            sx={{
              flexGrow: 1,
              '& .MuiOutlinedInput-root': {
                borderRadius: 8,
                backgroundColor: '#fff',
                '& .MuiOutlinedInput-input': {
                  textAlign: 'center',
                  padding: '8px',
                  fontSize: isMobile ? '1rem' : '1.25rem',
                  fontWeight: 500
                },
                '& .MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(0, 0, 0, 0.2)',
                },
              },
              '& input[type=number]::-webkit-inner-spin-button, & input[type=number]::-webkit-outer-spin-button': {
                WebkitAppearance: 'none',
                margin: 0
              },
              '& input[type=number]': {
                MozAppearance: 'textfield'
              }
            }}
          />
        ) : (
          <Box
            onClick={() => startEditingDimension(name, value)}
            sx={{
              flexGrow: 1,
              textAlign: 'center',
              fontSize: isMobile ? '1rem' : '1.25rem',
              fontWeight: 500,
              cursor: 'text',
              padding: '6px 0',
              borderRadius: 2,
              backgroundColor: '#fff',
              border: '1px solid rgba(0, 0, 0, 0.2)',
              transition: 'all 0.2s ease',
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.02)'
              }
            }}
          >
            {value}
          </Box>
        )}
        <Button
          variant="outlined"
          onClick={() => handleStepperClick(name, step, min, max)}
          sx={{
            minWidth: isExtraSmallMobile ? '30px' : '40px',
            width: isExtraSmallMobile ? '30px' : '40px',
            height: isExtraSmallMobile ? '34px' : '40px',
            padding: 0,
            borderRadius: '8px',
            fontSize: '1.2rem',
            fontWeight: 'bold',
            color: 'text.secondary',
            borderColor: 'rgba(0, 0, 0, 0.2)',
            backgroundColor: '#f5f5f5',
            ml: 1,
            '&:hover': {
              borderColor: 'primary.main',
              backgroundColor: 'rgba(0, 0, 0, 0.05)',
            }
          }}
        >
          +
        </Button>
      </Box>
      {helperText && (
        <Typography variant="caption" color="text.secondary">
          {helperText}
        </Typography>
      )}
    </div>
  );

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3.5 }}>
        <Typography variant="h5" gutterBottom sx={{
          fontWeight: 600,
          mt: 1,
          color: 'text.primary',
          fontSize: isMobile ? '1.25rem' : '1.5rem'
        }}>
          {t('simulationParameters')}
        </Typography>
        <Tooltip title={t('printerSpecifications')}>
          <Box
            component="span"
            onClick={handleInfoClick}
            sx={{
              cursor: 'pointer',
              color: 'text.secondary',
              transition: 'all 0.2s',
              '&:hover': {
                color: 'primary.main',
              },
              display: 'flex',
              alignItems: 'center'
            }}
          >
            <InfoIcon />
          </Box>
        </Tooltip>
        <PrinterInfoPopover
          open={infoPopoverOpen}
          anchorEl={infoAnchorEl}
          handleClose={handleInfoClose}
          initialInvestment={inputs.initialInvestment} // Pass current initialInvestment
          onInitialInvestmentChange={handleInitialInvestmentChange} // Pass handler
        />
      </Box>

      <Fade in={true} timeout={500}>
        <Card
          sx={{
            mb: 3,
            overflow: 'visible',
            borderRadius: 3,
            boxShadow: '0 2px 6px rgba(0, 0, 0, 0.08)',
            transition: 'transform 0.3s ease, boxShadow 0.3s ease',
            '&:hover': {
              transform: 'translateY(-2px)',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
            }
          }}
        >
          <CardContent sx={{ px: { xs: 2, sm: 3 }, py: 2.5 }}>
            <SectionHeader icon={<DimensionsIcon />} title={t('productDimensions')} />
            <Grid container spacing={isMobile ? 2 : 3}>
              <Grid item xs={12} sm={6}> {/* Changed from xs={6} for better mobile stacking */}
                <DimensionInput
                  label={t('shortEdge')}
                  value={inputs.shortEdge}
                  name="shortEdge"
                  min={10}
                  max={305}
                  helperText={`${t('range')}: 10-305mm`}
                />
              </Grid>
              <Grid item xs={12} sm={6}> {/* Changed from xs={6} for better mobile stacking */}
                <DimensionInput
                  label={t('longEdge')}
                  value={inputs.longEdge}
                  name="longEdge"
                  min={10}
                  max={458}
                  helperText={`${t('range')}: 10-458mm`}
                />
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Fade>

      <Fade in={true} timeout={600}>
        <Card
          sx={{
            mb: 3,
            overflow: 'visible',
            borderRadius: 3,
            boxShadow: '0 2px 6px rgba(0, 0, 0, 0.08)',
            transition: 'transform 0.3s ease, boxShadow 0.3s ease',
            '&:hover': {
              transform: 'translateY(-2px)',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
            }
          }}
        >
          <CardContent sx={{ px: { xs: 2, sm: 3 }, py: 2.5 }}>
            <SectionHeader icon={<VolumeIcon />} title={t('monthlySalesTarget')} />
            <Box sx={{ px: { xs: 0, sm: 2 }, mt: 2 }}>
              <Grid container spacing={2} alignItems="center" direction={isMobile ? "column" : "row"}>
                <Grid item xs={12} sm={isMobile ? 12 : 8}> {/* Full width on mobile */}
                  <VisibleSlider
                    value={typeof inputs.monthlySalesVolume === 'number' ? inputs.monthlySalesVolume : 0}
                    onChange={(_, value) => onInputChange('monthlySalesVolume', value)}
                    min={0}
                    max={1000}
                    step={10}
                    valueLabelDisplay="auto"
                  />
                </Grid>
                <Grid item xs={12} sm={isMobile ? 12 : 4}> {/* Full width on mobile */}
                  <TextField
                    value={(focusedInput === 'monthlySalesVolume' && editingValues['monthlySalesVolume'] !== undefined) 
                      ? editingValues['monthlySalesVolume'] 
                      : inputs.monthlySalesVolume}
                    onChange={(e) => handleNumericChange('monthlySalesVolume', e.target.value, 0, 1000)}
                    onFocus={() => handleFocus('monthlySalesVolume')}
                    onBlur={() => handleBlur('monthlySalesVolume', 0, 1000)}
                    inputProps={{
                      min: 0,
                      max: 1000,
                      style: { 
                        textAlign: 'center', 
                        fontWeight: '500', 
                        fontSize: isMobile ? '1rem' : '1.1rem' 
                      }
                    }}
                    type="text" // Changed to text to allow formatted numbers
                    size="small"
                    fullWidth
                    variant="outlined"
                    sx={{
                      mt: isMobile ? 1 : 0, // Add margin top on mobile if slider is above
                      '& .MuiOutlinedInput-root': {
                        fontWeight: 500,
                        borderRadius: '12px',
                        height: '48px',
                        backgroundColor: '#fff',
                        transition: 'all 0.2s ease-in-out',
                        '& .MuiOutlinedInput-notchedOutline': {
                          borderColor: 'rgba(0, 0, 0, 0.2)',
                        },
                        '&.Mui-focused': {
                          '& .MuiOutlinedInput-notchedOutline': {
                            borderColor: theme.palette.primary.main,
                            borderWidth: 1
                          }
                        }
                      },
                      '& input::-webkit-inner-spin-button, & input::-webkit-outer-spin-button': {
                        WebkitAppearance: 'none',
                        margin: 0
                      },
                      '& input[type=number]': {
                        MozAppearance: 'textfield'
                      }
                    }}
                  />
                </Grid>
              </Grid>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1, textAlign: 'center', fontWeight: 500, mb: 2 }}>
                {t('unitsPerMonth')} (0-1000)
              </Typography>
              <Grid container spacing={3}> {/* This was spacing={3} */}
                <Grid item xs={12}>
                  <StepperInput
                    label={`${t('salesPricePerUnit')} (${t('currency')})`}
                    value={inputs.salesPricePerUnit}
                    name="salesPricePerUnit"
                    min={0}
                    step={50} // As per constants.js
                  />
                </Grid>
              </Grid>
            </Box>
          </CardContent>
        </Card>
      </Fade>

      <Fade in={true} timeout={700}>
        <Card
          sx={{
            mb: 3,
            overflow: 'visible',
            borderRadius: 3,
            boxShadow: '0 2px 6px rgba(0, 0, 0, 0.08)',
            transition: 'transform 0.3s ease, boxShadow 0.3s ease',
            '&:hover': {
              transform: 'translateY(-2px)',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
            }
          }}
        >
          <CardContent sx={{ px: { xs: 2, sm: 3 }, py: 2.5 }}>
            <SectionHeader icon={<PriceIcon />} title={t('priceParameters')} />
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <StepperInput
                  label={`${t('materialCostPerUnit')} (${t('currency')})`}
                  value={inputs.materialCostPerUnit}
                  name="materialCostPerUnit"
                  min={0}
                  step={10} // As per constants.js
                />
              </Grid>
              <Grid item xs={12}>
                <StepperInput
                  label={`${t('laborCostPerHour')} (${t('currency')})`}
                  value={inputs.laborCostPerHour}
                  name="laborCostPerHour"
                  min={0}
                  step={100} // As per constants.js
                />
              </Grid>
              <Grid item xs={12}>
                <StepperInput
                  label={`${t('inkPrice')} (${t('currency')}/${t('cc')})`}
                  value={inputs.inkPricePerCC}
                  name="inkPricePerCC"
                  min={0}
                  step={1} // As per constants.js
                />
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Fade>
    </Box>
  );
};

export default SimulationForm; // Or InputPanel
