import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Grid, 
  Typography, 
  Box, 
  Paper,
  AppBar,
  Toolbar,
  Fade,
  useMediaQuery
} from '@mui/material';
import { useTheme } from '@mui/material/styles';
import InputPanel from './components/InputPanel/InputPanel';
import ResultsPanel from './components/ResultsPanel/ResultsPanel';
import { calculateAll, getPaybackStatus } from './utils/calculations';
import './App.css';

// Default values from PRD
const defaultInputs = {
  shortEdge: 90,
  longEdge: 90,
  salesPricePerUnit: 2500,
  monthlySalesVolume: 300,
  materialCostPerUnit: 200,
  laborCostPerHour: 2000,
  inkPricePerCC: 18
};

function App() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  // State for inputs and results
  const [inputs, setInputs] = useState(defaultInputs);
  const [results, setResults] = useState(null);
  const [hasCalculated, setHasCalculated] = useState(false);

  // Calculate results whenever inputs change
  useEffect(() => {
    const calculatedResults = calculateAll(inputs);
    setResults(calculatedResults.results);
    setHasCalculated(true);
  }, [inputs]);

  // Handle input changes
  const handleInputChange = (name, value) => {
    setInputs(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Get background color for results panel based on payback period
  const getResultsPanelBgColor = () => {
    if (!results) return theme.palette.background.paper;

    const status = getPaybackStatus(results.paybackPeriod);
    switch (status) {
      case 'good':
        return theme.palette.background.resultGood;
      case 'average':
        return theme.palette.background.resultAverage;
      case 'warning':
        return theme.palette.background.resultWarning;
      case 'no-profit':
        return theme.palette.background.resultNoProfit;
      default:
        return theme.palette.background.paper;
    }
  };

  // To help with the panel width/layout as specified in PRD
  const inputPanelWidth = isMobile ? 12 : 5; // 40-45% on desktop
  const resultsPanelWidth = isMobile ? 12 : 7; // 55-60% on desktop

  return (
    <Box className="app-wrapper">
      {/* App Bar */}
      <AppBar position="static" color="primary" elevation={0}>
        <Toolbar>
          <Box sx={{ display: 'flex', alignItems: 'center', width: '100%' }}>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontWeight: 'bold' }}>
              Roland DG
            </Typography>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Main Content */}
      <Container maxWidth="lg" className="app-container">
        <Box sx={{ my: { xs: 3, md: 4 }, textAlign: 'center' }}>
          <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 'bold' }}>
            MO-180 Sales Simulation Tool
          </Typography>
          <Typography variant="subtitle1" gutterBottom sx={{ color: theme.palette.text.secondary, maxWidth: 700, mx: 'auto', mb: 4 }}>
            Evaluate profitability and ROI for the Roland DG MO-180 UV printer
          </Typography>
        </Box>

        <Grid container spacing={4}>
          {/* Input Panel */}
          <Grid item xs={12} md={inputPanelWidth}>
            <Paper 
              elevation={2} 
              sx={{ 
                p: { xs: 2, sm: 3 }, 
                height: '100%',
                overflow: 'visible',
                transition: 'all 0.3s ease-in-out',
                '&:hover': {
                  boxShadow: 3
                }
              }}
            >
              <InputPanel 
                inputs={inputs} 
                onInputChange={handleInputChange} 
              />
            </Paper>
          </Grid>

          {/* Results Panel */}
          <Grid item xs={12} md={resultsPanelWidth}>
            <Fade in={hasCalculated} timeout={500}>
              <Paper 
                elevation={2} 
                sx={{ 
                  p: { xs: 2, sm: 3 }, 
                  height: '100%',
                  overflow: 'visible',
                  backgroundColor: getResultsPanelBgColor(),
                  transition: 'background-color 0.5s ease-in-out, box-shadow 0.3s ease-in-out',
                  '&:hover': {
                    boxShadow: 3
                  }
                }}
              >
                {results && (
                  <ResultsPanel results={results} />
                )}
              </Paper>
            </Fade>
          </Grid>
        </Grid>

        {/* Footer */}
        <Box 
          component="footer" 
          sx={{ 
            mt: 8, 
            pt: 3, 
            pb: 3, 
            textAlign: 'center',
            borderTop: `1px solid ${theme.palette.divider}`
          }}
        >
          <Typography variant="caption" color="text.secondary">
            Â© {new Date().getFullYear()} Roland DG Corporation. All rights reserved.
          </Typography>
        </Box>
      </Container>
    </Box>
  );
}

export default App;
