import React from 'react';
import { Box, Typography, TextField, Button, InputAdornment, useMediaQuery } from '@mui/material';
import { useTheme } from '@mui/material/styles';

/**
 * StepperInput Component
 * Reusable numeric input with increment/decrement buttons
 */
const StepperInput = ({
  label,
  value,
  name,
  endAdornment,
  min,
  max,
  helperText,
  error,
  startAdornment,
  step = 1,
  fullWidth = true,
  language = 'en-US',
  onValueChange,
  onFocus,
  onBlur
}) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  // Format value display based on language
  const displayValue = value === '' 
    ? '' 
    : Number(value).toLocaleString(language === 'ja' ? 'ja-JP' : 'en-US');

  // Handle direct text input changes
  const handleInputChange = (e) => {
    const rawValue = e.target.value.toString().replace(/,/g, '');

    // Allow clearing the field
    if (rawValue === '') {
      onValueChange(name, '');
      return;
    }

    // Validate and convert to number
    const numValue = parseFloat(rawValue);
    if (!isNaN(numValue)) {
      // Apply min/max constraints when provided
      if (min !== undefined && numValue < min) {
        onValueChange(name, min);
      } else if (max !== undefined && numValue > max) {
        onValueChange(name, max);
      } else {
        onValueChange(name, numValue);
      }
    }
  };

  // Handle stepper button clicks
  const handleStepperClick = (stepValue) => {
    const currentValue = parseFloat(value) || 0;
    let newValue = currentValue + stepValue;

    // Apply min/max constraints
    if (min !== undefined) newValue = Math.max(min, newValue);
    if (max !== undefined) newValue = Math.min(max, newValue);

    onValueChange(name, newValue);
  };

  // Format currency based on language
  // For Japanese, ensure currency symbol is at the end
  const formattedStartAdornment = language === 'ja' ? null : startAdornment;
  const formattedEndAdornment = language === 'ja' ? 
    (startAdornment || endAdornment) : endAdornment;

  return (
    <Box sx={{ mb: 2.5 }}>
      <Typography 
        variant="subtitle2" 
        gutterBottom 
        sx={{ fontWeight: 500, color: error ? 'error.main' : 'text.secondary' }}
      >
        {label}
      </Typography>
      <Box sx={{ 
        display: 'flex', 
        alignItems: 'center',
        width: '100%'
      }}>
        <Button
          variant="outlined"
          onClick={() => handleStepperClick(-step)}
          sx={{
            minWidth: isMobile ? '36px' : '44px',
            width: isMobile ? '36px' : '44px',
            height: '44px',
            padding: 0,
            borderRadius: '8px 0 0 8px',
            borderRight: 0,
            fontSize: '1.2rem',
            fontWeight: 'bold',
            color: '#666',
            borderColor: error ? 'error.main' : 'rgba(0, 0, 0, 0.2)',
            backgroundColor: '#f5f5f5',
            flexShrink: 0,
            '&:hover': {
              borderColor: error ? 'error.main' : 'primary.main',
              backgroundColor: 'rgba(0, 0, 0, 0.05)',
            }
          }}
        >
          −
        </Button>
        <TextField
          value={displayValue}
          onChange={handleInputChange}
          onFocus={onFocus ? () => onFocus(name) : undefined}
          onBlur={onBlur ? () => onBlur(name) : undefined}
          InputProps={{
            startAdornment: formattedStartAdornment && <InputAdornment position="start">{formattedStartAdornment}</InputAdornment>,
            endAdornment: formattedEndAdornment && <InputAdornment position="end">{formattedEndAdornment}</InputAdornment>,
          }}
          inputProps={{
            min,
            max,
            style: {
              textAlign: 'center',
              fontSize: '0.95rem',
              padding: '8px 0'
            }
          }}
          type="text"
          variant="outlined"
          size="medium"
          fullWidth
          error={Boolean(error)}
          sx={{
            flex: 1,
            '& .MuiOutlinedInput-root': {
              borderRadius: 0,
              fontWeight: 500,
              backgroundColor: '#fff',
              height: '44px',
              transition: 'all 0.2s ease-in-out',
              '& .MuiOutlinedInput-notchedOutline': {
                borderColor: error ? 'error.main' : 'rgba(0, 0, 0, 0.2)',
                borderLeftWidth: 0,
                borderRightWidth: 0,
              },
              '&:hover .MuiOutlinedInput-notchedOutline': {
                borderColor: error ? 'error.main' : 'primary.main',
              },
              '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                borderColor: error ? 'error.main' : 'primary.main',
                borderWidth: 1,
                borderLeftWidth: 0,
                borderRightWidth: 0,
              }
            },
            '& input::-webkit-inner-spin-button, & input::-webkit-outer-spin-button': {
              WebkitAppearance: 'none',
              margin: 0
            },
            '& input[type=number]': {
              MozAppearance: 'textfield'
            }
          }}
        />
        <Button
          variant="outlined"
          onClick={() => handleStepperClick(step)}
          sx={{
            minWidth: isMobile ? '36px' : '44px',
            width: isMobile ? '36px' : '44px',
            height: '44px',
            padding: 0,
            borderRadius: '0 8px 8px 0',
            borderLeft: 0,
            fontSize: '1.2rem',
            fontWeight: 'bold',
            color: '#666',
            borderColor: error ? 'error.main' : 'rgba(0, 0, 0, 0.2)',
            backgroundColor: '#f5f5f5',
            flexShrink: 0,
            '&:hover': {
              borderColor: error ? 'error.main' : 'primary.main',
              backgroundColor: 'rgba(0, 0, 0, 0.05)',
            }
          }}
        >
          +
        </Button>
      </Box>
      {helperText && (
        <Typography 
          variant="caption" 
          color={error ? 'error.main' : 'text.secondary'}
        >
          {helperText}
        </Typography>
      )}
      {error && (
        <Typography 
          variant="caption" 
          color="error"
          sx={{ display: 'block', mt: 0.5 }}
        >
          {error}
        </Typography>
      )}
    </Box>
  );
};

export default StepperInput;
