import React from 'react';
import { Box, Typography, Button, useMediaQuery, useTheme } from '@mui/material';

/**
 * Mobile Test Component
 * Displays a clear message to verify mobile styling is working
 */
const MobileTest = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <Box 
      sx={{ 
        position: 'fixed',
        bottom: 20,
        right: 20,
        zIndex: 9999,
        backgroundColor: 'red',
        color: 'white',
        padding: 2,
        borderRadius: 2,
        maxWidth: '80%',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
      }}
    >
      <Typography variant="h6" gutterBottom>
        {isMobile ? 'Mobile View Active' : 'Desktop View Active'}
      </Typography>
      <Typography variant="body2" gutterBottom>
        This message confirms the new version is running. The time is: {new Date().toLocaleTimeString()}
      </Typography>
      <Button 
        variant="contained" 
        color="primary"
        fullWidth
        sx={{ mt: 1, backgroundColor: 'white', color: 'red' }}
      >
        Test Button
      </Button>
    </Box>
  );
};

export default MobileTest;
