import React, { useState, useEffect } from 'react';
import { Container, Grid, Typography, Box, Paper } from '@mui/material';
import InputPanel from './components/InputPanel/InputPanel';
import ResultsPanel from './components/ResultsPanel/ResultsPanel';
import { calculateAll } from './utils/calculations';
import './App.css';

// Default values from PRD
const defaultInputs = {
  shortEdge: 90,
  longEdge: 90,
  salesPricePerUnit: 2500,
  monthlySalesVolume: 300,
  materialCostPerUnit: 200,
  laborCostPerHour: 2000,
  inkPricePerCC: 18
};

function App() {
  // State for inputs and results
  const [inputs, setInputs] = useState(defaultInputs);
  const [results, setResults] = useState(null);

  // Calculate results whenever inputs change
  useEffect(() => {
    const calculatedResults = calculateAll(inputs);
    setResults(calculatedResults.results);
  }, [inputs]);

  // Handle input changes
  const handleInputChange = (name, value) => {
    setInputs(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <Container maxWidth="lg" className="app-container">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" component="h1" align="center" gutterBottom>
          MO-180 Sales Simulation Tool
        </Typography>
        <Typography variant="subtitle1" align="center" gutterBottom>
          Evaluate profitability and ROI for the Roland DG MO-180 UV printer
        </Typography>
      </Box>

      <Grid container spacing={3}>
        {/* Input Panel (40% width) */}
        <Grid item xs={12} md={5}>
          <Paper elevation={3} sx={{ p: 2, height: '100%' }}>
            <InputPanel 
              inputs={inputs} 
              onInputChange={handleInputChange} 
            />
          </Paper>
        </Grid>

        {/* Results Panel (60% width) */}
        <Grid item xs={12} md={7}>
          <Paper 
            elevation={3} 
            sx={{ p: 2, height: '100%' }}
          >
            {results && (
              <ResultsPanel results={results} />
            )}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
