import React from 'react';
import {
  Popover,
  Typography,
  Box,
  useMediaQuery,
  useTheme,
  Button
} from '@mui/material';
import { useLanguage } from '../../../context/LanguageContext';

// Simple dialog component for mobile
const MobileDialog = ({ isOpen, onClose, title, children }) => {
  if (!isOpen) return null;

  return (
    <div 
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        zIndex: 9999,
        backgroundColor: 'white',
        padding: '20px',
        display: 'flex',
        flexDirection: 'column',
        overflowY: 'auto'
      }}
    >
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        marginBottom: '20px',
        borderBottom: '1px solid #eee',
        paddingBottom: '10px'
      }}>
        <h2 style={{ margin: 0, fontSize: '20px' }}>{title}</h2>
        <button 
          onClick={onClose}
          style={{
            background: 'none',
            border: 'none',
            fontSize: '24px',
            cursor: 'pointer',
            padding: '5px 10px'
          }}
        >
          ✕
        </button>
      </div>

      {/* Sample Image */}
      <div style={{ marginBottom: '20px', textAlign: 'center' }}>
        <img 
          src="/assets/images/lisa.png" 
          alt="Sample Print"
          style={{
            maxWidth: '100%',
            height: 'auto',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
          }}
        />
        <p style={{ 
          margin: '10px 0 0 0', 
          fontSize: '14px', 
          color: '#666' 
        }}>
          サンプル印刷
        </p>
      </div>

      {children}

      <div style={{ marginTop: 'auto', paddingTop: '20px' }}>
        <button
          onClick={onClose}
          style={{
            width: '100%',
            padding: '12px',
            backgroundColor: '#2196f3',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            fontSize: '18px',
            fontWeight: 'bold',
            cursor: 'pointer',
            boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
            letterSpacing: '1px'
          }}
        >
          閉じる (Close)
        </button>
      </div>
    </div>
  );
};

/**
 * PrinterInfoPopover Component
 * Displays printer specifications in a popover when info icon is clicked
 */
const PrinterInfoPopover = ({ open, anchorEl, handleClose }) => {
  const { t, language } = useLanguage();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  // Content for both desktop and mobile
  const InfoContent = () => (
    <div style={{ paddingBottom: '20px' }}>
      <h3 style={{ fontSize: '16px', marginTop: 0 }}>
        {t('baseParameters')}
      </h3>

      <div style={{ marginBottom: '16px' }}>
        <div style={{ 
          fontWeight: 'bold', 
          marginBottom: '6px' 
        }}>
          {t('initialInvestment')}:
        </div>
        <div style={{ fontSize: '16px' }}>
          {language === 'ja' ? `3,780,000${t('currency')}` : `${t('currency')} 3,780,000`}
        </div>
      </div>

      <div>
        <div style={{ 
          fontWeight: 'bold', 
          marginBottom: '6px' 
        }}>
          {t('printableArea')}:
        </div>
        <div style={{ fontSize: '16px' }}>
          305mm × 458mm
        </div>
      </div>
    </div>
  );

  // Use simple custom dialog for mobile
  if (isMobile) {
    return (
      <MobileDialog
        isOpen={open}
        onClose={handleClose}
        title={t('printerSpecifications')}
      >
        <InfoContent />
      </MobileDialog>
    );
  }

  // Desktop version uses Material UI Popover
  return (
    <Popover
      open={open}
      anchorEl={anchorEl}
      onClose={handleClose}
      anchorOrigin={{
        vertical: 'bottom',
        horizontal: 'right',
      }}
      transformOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      PaperProps={{
        sx: { 
          width: 'auto', 
          minWidth: 450, 
          maxWidth: 500, 
          p: 3,
          borderRadius: 2,
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)'
        }
      }}
    >
      <Typography variant="h6" gutterBottom sx={{ fontWeight: 600, borderBottom: '1px solid rgba(0, 0, 0, 0.1)', pb: 1, mb: 2 }}>
        {t('printerSpecifications')}
      </Typography>

      {/* Sample Image for Desktop */}
      <Box sx={{ mb: 2, textAlign: 'center' }}>
        <img 
          src="/assets/images/lisa.png" 
          alt="Sample Print"
          style={{
            maxWidth: '100%',
            height: 'auto',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
          }}
        />
        <Typography variant="caption" display="block" sx={{ mt: 1, color: 'text.secondary' }}>
          サンプル印刷
        </Typography>
      </Box>

      <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 600, color: 'text.primary', mb: 1 }}>
        {t('baseParameters')}
      </Typography>

      <Box sx={{ mb: 2.5 }}>
        <Box sx={{ 
          display: 'flex', 
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: 'center',
          mb: 2
        }}>
          <Typography variant="body2" component="span" sx={{ fontWeight: 500 }}>
            {t('initialInvestment')}:
          </Typography>
          <Typography variant="body2" component="span" sx={{ fontWeight: 600 }}>
            {language === 'ja' ? `3,780,000${t('currency')}` : `${t('currency')} 3,780,000`}
          </Typography>
        </Box>

        <Box sx={{ 
          display: 'flex', 
          flexDirection: 'row',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <Typography variant="body2" component="span" sx={{ fontWeight: 500 }}>
            {t('printableArea')}:
          </Typography>
          <Typography variant="body2" component="span" sx={{ fontWeight: 600 }}>
            305mm × 458mm
          </Typography>
        </Box>
      </Box>
    </Popover>
  );
};

export default PrinterInfoPopover;
