import React from 'react';
import { Box, Typography, Card, CardContent } from '@mui/material';
import { useLanguage } from '../../context/LanguageContext';
import LisaImage from '../../assets/images/lisa.png'; // Ensure the image is imported correctly

/**
 * SampleImagePanel Component
 * Displays sample image information for printing examples
 */
const SampleImagePanel = () => {
  const { language } = useLanguage();

  return (
    <Card sx={{ 
      mb: 3, 
      overflow: 'visible',
      borderRadius: 3,
      boxShadow: '0 2px 6px rgba(0, 0, 0, 0.08)',
      transition: 'transform 0.3s ease, box-shadow 0.3s ease',
      '&:hover': {
        transform: 'translateY(-2px)',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
      }
    }}>
      <CardContent sx={{ px: 3, py: 2.5 }}>
        <Box sx={{ width: '100%', textAlign: 'center', mb: 2 }}>
          <img 
            src={LisaImage} 
            alt="Lisa Sample" 
            style={{ 
              maxWidth: '100%', 
              height: 'auto', 
              borderRadius: '8px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
            }} 
          />
        </Box>

        <Typography 
          variant="body2" 
          align="center" 
          sx={{ 
            fontWeight: 500, 
            color: 'text.secondary',
            mt: 1
          }}
        >
          {language === 'ja' 
            ? 'このデータを使用して印刷しています'
            : 'This data is being used for printing'}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default SampleImagePanel;
