import React from 'react';
import { Box, Typography } from '@mui/material';
import { useTheme } from '@mui/material/styles';

/**
 * PaybackGauge Component
 * Displays a gauge chart showing the payback period with CMYK accent colors
 */
const PaybackGauge = ({ paybackPeriod }) => {
  const theme = useTheme();

  // Handle "no profit" case
  const noProfitCase = paybackPeriod === '-';

  // Convert payback period to a percentage for the gauge
  // We'll consider 60 months (5 years) as 100%
  const maxMonths = 60;
  const percentage = noProfitCase ? 100 : Math.min((paybackPeriod / maxMonths) * 100, 100);

  // Determine color based on payback period
  const getGaugeColor = () => {
    if (noProfitCase) return theme.palette.grey[400];
    if (paybackPeriod <= 12) return theme.palette.success.main;
    if (paybackPeriod <= 24) return theme.palette.warning.main;
    return theme.palette.error.main;
  };

  // Add a subtle cyan accent as specified in PRD
  const getAccentColor = () => {
    if (noProfitCase) return theme.palette.grey[300];
    return theme.palette.cmyk.cyan; // Subtle cyan accent for Payback chart
  };

  // Calculate the rotation for the gauge needle
  const needleRotation = noProfitCase ? 180 : (percentage * 1.8); // 180 degrees total rotation

  return (
    <Box sx={{ width: '100%', height: '100%', position: 'relative', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <Typography variant="h6" align="center" gutterBottom>
        Investment Payback Period
      </Typography>

      {/* Gauge */}
      <Box sx={{ position: 'relative', width: '240px', height: '120px', mt: 2 }}>
        {/* Gauge background with gradient */}
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            borderRadius: '120px 120px 0 0',
            background: 'linear-gradient(to right, #4CAF50 30%, #FF9800 30% 60%, #F44336 60%)',
            opacity: 0.2,
          }}
        />

        {/* Cyan accent border for the gauge */}
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            borderRadius: '120px 120px 0 0',
            border: `2px solid ${getAccentColor()}`,
            borderBottom: 'none',
            opacity: 0.4,
          }}
        />

        {/* Gauge fill */}
        <Box
          sx={{
            position: 'absolute',
            bottom: 0,
            left: '50%',
            transform: 'translateX(-50%)',
            width: '16px',
            height: '120px',
            transformOrigin: 'bottom center',
            background: `${getGaugeColor()}`,
            borderRadius: '8px 8px 0 0',
            transform: `translateX(-50%) rotate(${needleRotation - 90}deg)`,
            transition: 'transform 1s cubic-bezier(0.34, 1.56, 0.64, 1)',
            '&::after': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: '50%',
              transform: 'translate(-50%, -50%)',
              width: '16px',
              height: '16px',
              borderRadius: '50%',
              backgroundColor: getGaugeColor(),
            }
          }}
        />

        {/* Enhanced gauge ticks and labels */}
        <Box sx={{ position: 'absolute', bottom: '-10px', left: 0, width: '100%', display: 'flex', justifyContent: 'space-between' }}>
          <Box sx={{ position: 'relative', width: 0 }}>
            <Typography variant="caption" sx={{ position: 'absolute', left: 0, transform: 'translateX(-50%)', fontWeight: 500 }}>0</Typography>
          </Box>
          {/* 12 months marker */}
          <Box sx={{ position: 'relative', width: 0, left: '-21%' }}>
            <Typography variant="caption" sx={{ position: 'absolute', left: '36%', transform: 'translateX(-50%)', fontWeight: 500, color: theme.palette.success.main }}>12</Typography>
          </Box>
          {/* 24 months marker */}
          <Box sx={{ position: 'relative', width: 0, left: '-10%' }}>
            <Typography variant="caption" sx={{ position: 'absolute', left: '60%', transform: 'translateX(-50%)', fontWeight: 500, color: theme.palette.warning.main }}>24</Typography>
          </Box>
          <Box sx={{ position: 'relative', width: 0 }}>
            <Typography variant="caption" sx={{ position: 'absolute', right: '-50%', transform: 'translateX(-50%)', fontWeight: 500 }}>{maxMonths}</Typography>
          </Box>
        </Box>

        {/* Center pivot point with Cyan accent */}
        <Box
          sx={{
            position: 'absolute',
            bottom: 0,
            left: '50%',
            transform: 'translateX(-50%)',
            width: '20px',
            height: '20px',
            borderRadius: '50%',
            backgroundColor: theme.palette.grey[300],
            border: `3px solid ${theme.palette.cmyk.cyan}`,
            boxShadow: theme.shadows[2],
          }}
        />
      </Box>

      {/* Payback period value with animation */}
      <Box sx={{ mt: 4, textAlign: 'center', animation: 'fadeIn 0.5s ease-out' }}>
        <Typography 
          variant="h4" 
          fontWeight="bold" 
          color={getGaugeColor()}
          sx={{ 
            transition: 'color 0.5s ease',
            animation: 'fadeNumber 0.5s ease-out'
          }}
          className="animate-number"
        >
          {noProfitCase ? 'No Profit' : `${paybackPeriod.toFixed(1)} months`}
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
          {noProfitCase 
            ? 'Current parameters do not yield profit' 
            : paybackPeriod <= 12 
              ? 'Excellent payback period' 
              : paybackPeriod <= 24 
                ? 'Good payback period' 
                : 'Extended payback period'}
        </Typography>
      </Box>
    </Box>
  );
};

export default PaybackGauge;
