// MO Simulator 2/src/features/simulation/SimulationForm.js
// OR MO Simulator 2/src/components/InputPanel/InputPanel.js

import React, { useState, useEffect, useRef } from 'react';
import {
  Box,
  Typography,
  TextField,
  Slider,
  Grid,
  InputAdornment,
  Card,
  CardContent,
  Tooltip,
  Button,
  Fade,
  useMediaQuery,
  styled
} from '@mui/material';
import { useTheme } from '@mui/material/styles';
import { useLanguage } from '../../context/LanguageContext';
// Correct the path based on your project structure
import PrinterInfoPopover from './components/PrinterInfoPopover';

// Using SVG icons directly instead of emojis to match the design
const DimensionsIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M5 5L9 9" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M19 5L15 9" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M5 19L9 15" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M19 19L15 15" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M5 5L3 3" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M19 5L21 3" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M5 19L3 21" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M19 19L21 21" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
  </svg>
);

const PriceIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="9" stroke="currentColor" strokeWidth="2"/>
    <path d="M12 7V17" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
    <path d="M8 12H16" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
  </svg>
);

const VolumeIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M3 17H7V10H3V17Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M10 17H14V3H10V17Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M17 17H21V6H17V17Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M3 21H21" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
  </svg>
);

const InfoIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/>
    <path d="M12 16V12" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
    <circle cx="12" cy="8" r="1" fill="currentColor"/>
  </svg>
);

// Common button style to ensure consistency across all number controls
const NumberButton = styled(Button)(({ theme }) => ({
  minWidth: '44px',
  width: '44px',
  height: '44px',
  padding: 0,
  borderRadius: '8px',
  fontSize: '1.2rem',
  fontWeight: '700',
  color: '#666',
  border: '1px solid rgba(0, 0, 0, 0.2)',
  backgroundColor: '#f5f5f5',
  '&:hover': {
    backgroundColor: '#e0e0e0',
  }
}));

// Common input style to ensure consistency across all number controls
const NumberInput = styled(TextField)(({ theme }) => ({
  flex: '1',
  '& .MuiInputBase-root': {
    height: '44px',
    backgroundColor: '#fff',
  },
  '& .MuiOutlinedInput-input': {
    textAlign: 'center',
    fontSize: '1rem',
    fontWeight: '500',
    padding: '8px',
  },
  '& .MuiOutlinedInput-notchedOutline': {
    borderColor: 'rgba(0, 0, 0, 0.2)',
    borderRadius: '8px',
  },
  '& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline': {
    borderColor: '#2471CC',
    borderWidth: '1px',
  },
  '& input::-webkit-inner-spin-button, & input::-webkit-outer-spin-button': {
    WebkitAppearance: 'none',
    margin: 0
  },
  '& input[type=number]': {
    MozAppearance: 'textfield'
  }
}));

// Custom slider with enhanced visibility
const VisibleSlider = styled(Slider)({
  color: '#2471CC',
  height: 8,
  padding: '13px 0',
  '& .MuiSlider-track': {
    height: 8,
    borderRadius: 4,
    backgroundColor: '#2471CC',
  },
  '& .MuiSlider-rail': {
    height: 8,
    borderRadius: 4,
    backgroundColor: '#d1d1d1',
  },
  '& .MuiSlider-thumb': {
    height: 24,
    width: 24,
    backgroundColor: '#fff',
    border: '2px solid #2471CC',
    boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
    '&:focus, &:hover, &.Mui-active': {
      boxShadow: '0 0 0 8px rgba(36, 113, 204, 0.2)',
    },
  },
});

/**
 * SimulationForm Component / InputPanel Component
 * Handles user inputs for the simulation with enhanced styling and improved aesthetics
 */
const SimulationForm = ({ inputs, onInputChange }) => { // Or InputPanel
  const theme = useTheme();
  const { t, language } = useLanguage(); // Now we get language from useLanguage
  const isMobile = useMediaQuery(theme.breakpoints.down('sm')); // For responsive styling
  const isExtraSmallMobile = useMediaQuery('(max-width:380px)');

  // Local state for input values during editing
  const [localInputs, setLocalInputs] = useState({});
  const [isEditing, setIsEditing] = useState({});
  const inputRefs = useRef({});

  useEffect(() => {
    // Initialize local inputs with parent values when they change
    // Only update if not actively editing to preserve focus
    Object.keys(inputs).forEach(key => {
      if (!isEditing[key]) {
        setLocalInputs(prev => ({
          ...prev,
          [key]: inputs[key]
        }));
      }
    });
  }, [inputs, isEditing]);

  const [infoAnchorEl, setInfoAnchorEl] = useState(null);
  const infoPopoverOpen = Boolean(infoAnchorEl);

  const [editingDimension, setEditingDimension] = useState(null);
  const [tempDimensionValue, setTempDimensionValue] = useState("");

  const handleInfoClick = (event) => {
    setInfoAnchorEl(event.currentTarget);
  };

  const handleInfoClose = () => {
    setInfoAnchorEl(null);
  };

  // This function will be passed to PrinterInfoPopover
  // It calls the onInputChange from props, which ultimately updates the useSimulation hook
  const handleInitialInvestmentChange = (value) => {
    onInputChange('initialInvestment', value);
  };

  // Handle local input changes without triggering parent updates immediately
  const handleLocalInputChange = (name, value) => {
    setLocalInputs(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Start editing a numeric input
  const handleInputFocus = (name) => {
    setIsEditing(prev => ({
      ...prev,
      [name]: true
    }));

    // Set initial local value if not set
    if (localInputs[name] === undefined) {
      setLocalInputs(prev => ({
        ...prev, 
        [name]: inputs[name]
      }));
    }

    // Select all text on focus for easy editing
    if (inputRefs.current[name]) {
      setTimeout(() => {
        inputRefs.current[name].select();
      }, 10);
    }
  };

  // Commit changes to parent when done editing
  const handleInputBlur = (name, min, max) => {
    setIsEditing(prev => ({
      ...prev,
      [name]: false
    }));

    let finalValue = localInputs[name];

    // Process and validate the value
    if (typeof finalValue === 'string') {
      const rawValue = finalValue.toString().replace(/,/g, '');

      if (rawValue === '') {
        finalValue = '';
      } else {
        const numValue = parseFloat(rawValue);
        if (!isNaN(numValue)) {
          if (min !== undefined) finalValue = Math.max(min, numValue);
          if (max !== undefined) finalValue = Math.min(max, numValue);
        } else {
          // If not a valid number, revert to previous input value
          finalValue = inputs[name];
        }
      }
    }

    // Only update parent if value has changed
    if (finalValue !== inputs[name]) {
      onInputChange(name, finalValue);
    }
  };

  // Handle stepper button clicks (+/-)
  const handleStepperClick = (name, step, min, max) => {
    const currentValue = parseFloat(inputs[name]) || 0;
    let newValue = currentValue + step;

    if (min !== undefined) newValue = Math.max(min, newValue);
    if (max !== undefined) newValue = Math.min(max, newValue);

    // Update directly through parent since this is a button click
    onInputChange(name, newValue);
  };

  const startEditingDimension = (name, currentValue) => {
    setEditingDimension(name);
    setTempDimensionValue(currentValue.toString());
  };

  const handleDimensionChange = (e) => {
    setTempDimensionValue(e.target.value);
  };

  const finishEditingDimension = (name, min, max) => {
    const value = parseInt(tempDimensionValue, 10);
    if (!isNaN(value) && value >= min && value <= max) {
      onInputChange(name, value);
    } else {
      // Revert to original value if input is invalid
      setTempDimensionValue(inputs[name].toString());
    }
    setEditingDimension(null);
  };

  const handleDimensionKeyDown = (e, name, min, max) => {
    if (e.key === 'Enter') {
      finishEditingDimension(name, min, max);
      e.target.blur();
    } else if (e.key === 'Escape') {
      setTempDimensionValue(inputs[name].toString()); // Revert on escape
      setEditingDimension(null);
      e.target.blur();
    }
  };

  // Common StepperInput used for all numeric inputs to ensure consistency
  const StepperInput = ({
    label,
    name,
    min,
    max,
    helperText,
    step = 1
  }) => {
    // Use local value while editing, or parent value when not editing
    const displayValue = isEditing[name] 
      ? localInputs[name] 
      : inputs[name] === '' 
        ? '' 
        : Number(inputs[name]).toLocaleString(language === 'ja' ? 'ja-JP' : 'en-US');

    return (
      <Box sx={{ mb: 3 }}>
        <Typography variant="subtitle2" gutterBottom sx={{ fontWeight: 500, color: 'text.secondary', mb: 1 }}>
          {label}
        </Typography>
        <Box sx={{ 
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          width: '100%',
          gap: '10px' // Consistent spacing
        }}>
          <NumberButton 
            onClick={() => handleStepperClick(name, -step, min, max)}
          >
            －
          </NumberButton>

          <NumberInput
            inputRef={el => inputRefs.current[name] = el}
            value={displayValue}
            onChange={(e) => handleLocalInputChange(name, e.target.value)}
            onFocus={() => handleInputFocus(name)}
            onBlur={() => handleInputBlur(name, min, max)}
            variant="outlined"
            size="medium"
            inputProps={{
              style: { textAlign: 'center', fontWeight: '500' }
            }}
          />

          <NumberButton 
            onClick={() => handleStepperClick(name, step, min, max)}
          >
            ＋
          </NumberButton>
        </Box>
        {helperText && (
          <Typography variant="caption" color="text.secondary" sx={{ mt: 0.5, display: 'block' }}>
            {helperText}
          </Typography>
        )}
      </Box>
    );
  };

  // Dimension input component
  const DimensionInput = ({
    label,
    value,
    name,
    min,
    max,
    helperText,
    step = 5
  }) => (
    <div>
      <Typography variant="subtitle2" gutterBottom sx={{ fontWeight: 500, color: 'text.secondary', mb: 1 }}>
        {label} (mm)
      </Typography>
      <Box sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        mb: 1,
        gap: '10px' // Consistent spacing
      }}>
        <NumberButton
          onClick={() => handleStepperClick(name, -step, min, max)}
        >
          －
        </NumberButton>
        {editingDimension === name ? (
          <NumberInput
            autoFocus
            value={tempDimensionValue}
            onChange={handleDimensionChange}
            onBlur={() => finishEditingDimension(name, min, max)}
            onKeyDown={(e) => handleDimensionKeyDown(e, name, min, max)}
            variant="outlined"
            size="small"
            type="number"
            inputProps={{
              min,
              max,
              style: { textAlign: 'center' }
            }}
          />
        ) : (
          <Box
            onClick={() => startEditingDimension(name, value)}
            sx={{
              flexGrow: 1,
              textAlign: 'center',
              fontSize: '1rem',
              fontWeight: 500,
              cursor: 'text',
              padding: '10px 0',
              borderRadius: '8px',
              border: '1px solid rgba(0, 0, 0, 0.2)',
              backgroundColor: '#fff',
              transition: 'all 0.2s ease',
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.02)'
              }
            }}
          >
            {value}
          </Box>
        )}
        <NumberButton
          onClick={() => handleStepperClick(name, step, min, max)}
        >
          ＋
        </NumberButton>
      </Box>
      {helperText && (
        <Typography variant="caption" color="text.secondary">
          {helperText}
        </Typography>
      )}
    </div>
  );

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3.5 }}>
        <Typography variant="h5" gutterBottom sx={{
          fontWeight: 600,
          mt: 1,
          color: 'text.primary',
          fontSize: isMobile ? '1.25rem' : '1.5rem'
        }}>
          {t('simulationParameters')}
        </Typography>
        <Tooltip title={t('printerSpecifications')}>
          <Box
            component="span"
            onClick={handleInfoClick}
            sx={{
              cursor: 'pointer',
              color: 'text.secondary',
              transition: 'all 0.2s',
              '&:hover': {
                color: 'primary.main',
              },
              display: 'flex',
              alignItems: 'center'
            }}
          >
            <InfoIcon />
          </Box>
        </Tooltip>
        <PrinterInfoPopover
          open={infoPopoverOpen}
          anchorEl={infoAnchorEl}
          handleClose={handleInfoClose}
          initialInvestment={inputs.initialInvestment} // Pass current initialInvestment
          onInitialInvestmentChange={handleInitialInvestmentChange} // Pass handler
        />
      </Box>

      <Fade in={true} timeout={500}>
        <Card
          sx={{
            mb: 3,
            overflow: 'visible',
            borderRadius: 3,
            boxShadow: '0 2px 6px rgba(0, 0, 0, 0.08)',
            transition: 'transform 0.3s ease, boxShadow 0.3s ease',
            '&:hover': {
              transform: 'translateY(-2px)',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
            }
          }}
        >
          <CardContent sx={{ px: { xs: 2, sm: 3 }, py: 2.5 }}>
            <SectionHeader icon={<DimensionsIcon />} title={t('productDimensions')} />
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <DimensionInput
                  label={t('shortEdge')}
                  value={inputs.shortEdge}
                  name="shortEdge"
                  min={10}
                  max={305}
                  helperText={`${t('range')}: 10-305mm`}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <DimensionInput
                  label={t('longEdge')}
                  value={inputs.longEdge}
                  name="longEdge"
                  min={10}
                  max={458}
                  helperText={`${t('range')}: 10-458mm`}
                />
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Fade>

      <Fade in={true} timeout={600}>
        <Card
          sx={{
            mb: 3,
            overflow: 'visible',
            borderRadius: 3,
            boxShadow: '0 2px 6px rgba(0, 0, 0, 0.08)',
            transition: 'transform 0.3s ease, boxShadow 0.3s ease',
            '&:hover': {
              transform: 'translateY(-2px)',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
            }
          }}
        >
          <CardContent sx={{ px: { xs: 2, sm: 3 }, py: 2.5 }}>
            <SectionHeader icon={<VolumeIcon />} title={t('monthlySalesTarget')} />
            <Box sx={{ px: { xs: 0, sm: 2 }, mt: 2 }}>
              <Box sx={{ mb: 3 }}>
                <VisibleSlider
                  value={typeof inputs.monthlySalesVolume === 'number' ? inputs.monthlySalesVolume : 0}
                  onChange={(_, value) => onInputChange('monthlySalesVolume', value)}
                  min={0}
                  max={1000}
                  step={10}
                  valueLabelDisplay="auto"
                />
                <Box sx={{ mt: 2, display: 'flex', justifyContent: 'center' }}>
                  <NumberInput
                    inputRef={el => inputRefs.current['monthlySalesVolume'] = el}
                    value={isEditing['monthlySalesVolume'] 
                      ? localInputs['monthlySalesVolume'] 
                      : inputs.monthlySalesVolume}
                    onChange={(e) => handleLocalInputChange('monthlySalesVolume', e.target.value)}
                    onFocus={() => handleInputFocus('monthlySalesVolume')}
                    onBlur={() => handleInputBlur('monthlySalesVolume', 0, 1000)}
                    variant="outlined"
                    size="medium"
                    sx={{ width: '100%', maxWidth: '300px' }}
                    inputProps={{
                      style: { textAlign: 'center', fontWeight: '500' }
                    }}
                  />
                </Box>
              </Box>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1, textAlign: 'center', fontWeight: 500, mb: 2 }}>
                {t('unitsPerMonth')} (0-1000)
              </Typography>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <StepperInput
                    label={`${t('salesPricePerUnit')} (${t('currency')})`}
                    name="salesPricePerUnit"
                    min={0}
                    step={50}
                  />
                </Grid>
              </Grid>
            </Box>
          </CardContent>
        </Card>
      </Fade>

      <Fade in={true} timeout={700}>
        <Card
          sx={{
            mb: 3,
            overflow: 'visible',
            borderRadius: 3,
            boxShadow: '0 2px 6px rgba(0, 0, 0, 0.08)',
            transition: 'transform 0.3s ease, boxShadow 0.3s ease',
            '&:hover': {
              transform: 'translateY(-2px)',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
            }
          }}
        >
          <CardContent sx={{ px: { xs: 2, sm: 3 }, py: 2.5 }}>
            <SectionHeader icon={<PriceIcon />} title={t('priceParameters')} />
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <StepperInput
                  label={`${t('materialCostPerUnit')} (${t('currency')})`}
                  name="materialCostPerUnit"
                  min={0}
                  step={10}
                />
              </Grid>
              <Grid item xs={12}>
                <StepperInput
                  label={`${t('laborCostPerHour')} (${t('currency')})`}
                  name="laborCostPerHour"
                  min={0}
                  step={100}
                />
              </Grid>
              <Grid item xs={12}>
                <StepperInput
                  label={`${t('inkPrice')} (${t('currency')}/${t('cc')})`}
                  name="inkPricePerCC"
                  min={0}
                  step={1}
                />
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Fade>
    </Box>
  );
};

