import React, { useState } from 'react';
import { 
  Popover, 
  Typography, 
  Box,
  TextField,
  InputAdornment,
  Divider
} from '@mui/material';
import { useLanguage } from '../../../context/LanguageContext';

/**
 * PrinterInfoPopover Component
 * Displays printer specifications in a popover when info icon is clicked
 * Now with directly editable Initial Investment parameter
 * And sample image display
 */
const PrinterInfoPopover = ({ open, anchorEl, handleClose, initialInvestment, onInitialInvestmentChange }) => {
  const { t, language } = useLanguage(); // Get translation function and current language

  // State for input value
  const [tempValue, setTempValue] = useState(initialInvestment);
  // State to track image load failures
  const [imageError, setImageError] = useState(false);

  // Format number with commas for display
  const formatNumber = (num) => {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  // Handle input change
  const handleInputChange = (event) => {
    // Remove commas and convert to number
    const value = parseInt(event.target.value.replace(/,/g, ''), 10);
    setTempValue(isNaN(value) ? '' : value);
  };

  // Handle blur to save value
  const handleBlur = () => {
    if (!isNaN(tempValue) && tempValue > 0) {
      onInitialInvestmentChange(tempValue);
    } else {
      // Reset to current value if invalid
      setTempValue(initialInvestment);
    }
  };

  // Handle key press events
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      if (!isNaN(tempValue) && tempValue > 0) {
        onInitialInvestmentChange(tempValue);
      } else {
        // Reset to current value if invalid
        setTempValue(initialInvestment);
      }
      event.target.blur();
    }
  };

  // Updated text for the sample image section
  const captionText = language === 'ja' 
    ? '全てのシミュレーションは、この画像を基に算出しています'
    : 'Simulations are based on the above image';

  // Try multiple image paths
  const imagePaths = [
    '/assets/images/lisa.png',                          // Relative to public folder
    `${process.env.PUBLIC_URL}/assets/images/lisa.png`, // Using PUBLIC_URL
    '/lisa.png',                                        // Direct in public root
  ];

  return (
    <Popover
      open={open}
      anchorEl={anchorEl}
      onClose={handleClose}
      anchorOrigin={{
        vertical: 'bottom',
        horizontal: 'right',
      }}
      transformOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      PaperProps={{
        sx: { 
          width: 'auto', 
          minWidth: 450, 
          maxWidth: 500, 
          p: 3,
          borderRadius: 2,
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)'
        }
      }}
    >
      <Typography variant="h6" gutterBottom sx={{ fontWeight: 600, borderBottom: '1px solid rgba(0, 0, 0, 0.1)', pb: 1, mb: 2 }}>
        {t('printerSpecifications')}
      </Typography>

      {/* Sample Image Section */}
      <Box sx={{ mb: 2.5 }}>
        <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 600, color: 'text.primary', mb: 1 }}>
          {language === 'ja' ? 'サンプル印刷' : 'Sample Print'}
        </Typography>

        <Box sx={{ ml: 1 }}>
          {imageError ? (
            // Fallback colored div with Lisa text if all image sources fail
            <Box 
              sx={{
                width: '100%',
                height: '180px',
                borderRadius: '8px',
                background: 'linear-gradient(to right, #9733EE, #DA22FF)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                fontWeight: 'bold',
                fontSize: '24px',
                marginBottom: '12px',
                boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
              }}
            >
              Lisa
            </Box>
          ) : (
            // Try to display the image
            <Box 
              sx={{
                width: '100%',
                display: 'flex',
                justifyContent: 'center',
                mb: 1.5
              }}
            >
              <img 
                src={imagePaths[0]}
                alt="Lisa"
                style={{
                  maxWidth: '100%',
                  height: 'auto',
                  borderRadius: '8px',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
                }}
                onError={(e) => {
                  console.error("Image failed to load:", e.target.src);
                  // Try the next source if available
                  const currentIndex = imagePaths.indexOf(e.target.src);
                  if (currentIndex < imagePaths.length - 1) {
                    e.target.src = imagePaths[currentIndex + 1];
                  } else {
                    // If all sources fail, show the fallback
                    setImageError(true);
                  }
                }}
              />
            </Box>
          )}

          <Typography 
            variant="body2" 
            sx={{ 
              fontWeight: 500, 
              color: 'text.secondary',
              textAlign: 'center',
              mb: 1
            }}
          >
            {captionText}
          </Typography>
        </Box>
      </Box>

      <Divider sx={{ my: 2 }} />

      {/* Base Parameters Section */}
      <Box sx={{ mb: 2.5 }}>
        <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 600, color: 'text.primary', mb: 1 }}>
          {t('baseParameters')}
        </Typography>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1, ml: 1 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Typography variant="body2" sx={{ fontWeight: 500 }}>
              {t('initialInvestment')}:
            </Typography>
            <TextField
              value={tempValue}
              onChange={handleInputChange}
              onBlur={handleBlur}
              onKeyDown={handleKeyPress}
              size="small"
              type="text"
              InputProps={{
                startAdornment: language !== 'ja' && <InputAdornment position="start">{t('currency')}</InputAdornment>,
                endAdornment: language === 'ja' && <InputAdornment position="end">{t('currency')}</InputAdornment>,
                sx: { fontWeight: 600 }
              }}
              sx={{
                width: '200px',
                '& .MuiOutlinedInput-root': {
                  borderRadius: '8px',
                },
                // Remove inner arrows from number inputs
                '& input[type=number]::-webkit-inner-spin-button, & input[type=number]::-webkit-outer-spin-button': {
                  WebkitAppearance: 'none',
                  margin: 0
                },
                '& input[type=number]': {
                  MozAppearance: 'textfield'
                }
              }}
            />
          </Box>
          <Typography variant="body2" sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <span style={{ fontWeight: 500 }}>{t('printableArea')}:</span> 
            <span style={{ fontWeight: 600 }}>305mm × 458mm</span>
          </Typography>
        </Box>
      </Box>
    </Popover>
  );
};

export default PrinterInfoPopover;
