import React from 'react';
import { 
  Box, 
  Typography, 
  Grid, 
  Divider,
  Button,
  Paper,
  Fade
} from '@mui/material';
import { useTheme } from '@mui/material/styles';
import { getPaybackStatus } from '../../utils/calculations';

// Helper function to format numbers with commas as thousands separators
const formatNumber = (num, decimals = 0) => {
  if (typeof num === 'string') return num;
  return num.toLocaleString(undefined, { 
    minimumFractionDigits: decimals,
    maximumFractionDigits: decimals
  });
};

// Helper function to format JPY currency
const formatCurrency = (amount) => {
  return `JPY ${formatNumber(Math.round(amount))}`;
};

// Helper function to format percentage
const formatPercent = (value) => {
  return `${value.toFixed(2)}%`;
};

/**
 * Results Panel Component
 * Displays the calculated results with enhanced styling
 */
const ResultsPanel = ({ results }) => {
  const theme = useTheme();

  // Determine the background color based on payback period
  const paybackStatus = getPaybackStatus(results.paybackPeriod);

  // Title and message based on status
  const getStatusInfo = () => {
    switch(paybackStatus) {
      case 'good':
        return {
          title: 'Excellent Investment',
          message: 'Fast payback period under 12 months'
        };
      case 'average':
        return {
          title: 'Good Investment',
          message: 'Reasonable payback period between 13-24 months'
        };
      case 'warning':
        return {
          title: 'Consider Carefully',
          message: 'Longer payback period exceeding 24 months'
        };
      case 'no-profit':
        return {
          title: 'Not Profitable',
          message: 'Current parameters do not yield a profit'
        };
      default:
        return {
          title: 'Results',
          message: ''
        };
    }
  };

  const statusInfo = getStatusInfo();

  // Custom result card component
  const ResultCard = ({ title, value, large = false }) => (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle2" color="text.secondary" gutterBottom>
        {title}
      </Typography>
      <Typography
        variant={large ? "h4" : "h5"}
        component="div"
        fontWeight="bold"
        color="text.primary"
        sx={{ wordBreak: "break-word" }}
      >
        {value}
      </Typography>
    </Box>
  );

  // Small stat component
  const SmallStat = ({ title, value }) => (
    <Box>
      <Typography variant="body2" color="text.secondary" gutterBottom>
        {title}
      </Typography>
      <Typography variant="body1" fontWeight="bold">
        {value}
      </Typography>
    </Box>
  );

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h5" fontWeight={600}>
          Simulation Results
        </Typography>
        <Fade in={true}>
          <Paper 
            elevation={0} 
            sx={{ 
              px: 2, 
              py: 1, 
              bgcolor: paybackStatus === 'good' ? 'success.light' : 
                      paybackStatus === 'average' ? 'warning.light' :
                      paybackStatus === 'warning' ? 'error.light' : 'grey.200',
              borderRadius: '16px'
            }}
          >
            <Typography variant="subtitle2" fontWeight={600}>
              {statusInfo.title}
            </Typography>
          </Paper>
        </Fade>
      </Box>

      <Typography variant="body2" color="text.secondary" paragraph>
        {statusInfo.message}
      </Typography>

      {/* Main financial metrics */}
      <Grid container spacing={4} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6}>
          <ResultCard 
            title="Monthly Sales" 
            value={formatCurrency(results.monthlySales)} 
            large={true}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <ResultCard 
            title="Monthly Gross Profit" 
            value={formatCurrency(results.monthlyGrossProfit)} 
            large={true}
          />
        </Grid>
      </Grid>

      <Divider sx={{ my: 3 }} />

      {/* Key metrics */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6}>
          <ResultCard 
            title="Gross Profit Margin" 
            value={formatPercent(results.grossProfitMargin)}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <ResultCard 
            title="Investment Payback Period" 
            value={results.paybackPeriod === '-' ? 'N/A' : `${results.paybackPeriod.toFixed(1)} months`}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <ResultCard 
            title="Cost per Unit" 
            value={formatCurrency(results.costPerUnit)}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <ResultCard 
            title="Operating Hours (monthly)" 
            value={`${results.operatingHours.toFixed(1)} hours`}
          />
        </Grid>
      </Grid>

      <Divider sx={{ my: 3 }} />

      {/* Production details */}
      <Paper 
        elevation={0} 
        sx={{ 
          bgcolor: 'background.default', 
          p: 2, 
          borderRadius: 2,
          mb: 4
        }}
      >
        <Typography variant="subtitle2" fontWeight={600} gutterBottom>
          Production Details
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={6}>
            <SmallStat 
              title="Items per Print Job" 
              value={`${formatNumber(results.itemsPerPrintJob)} units`}
            />
          </Grid>
          <Grid item xs={6}>
            <SmallStat 
              title="Monthly Print Jobs" 
              value={`${formatNumber(results.monthlyPrintJobs)} jobs`}
            />
          </Grid>
        </Grid>
      </Paper>

      {/* Ink usage details */}
      <Paper 
        elevation={0} 
        sx={{ 
          bgcolor: 'background.default', 
          p: 2, 
          borderRadius: 2,
          mb: 3
        }}
      >
        <Typography variant="subtitle2" fontWeight={600} gutterBottom>
          Ink Usage per Item
        </Typography>

        <Grid container spacing={2} sx={{ mb: 2 }}>
          <Grid item xs={4}>
            <SmallStat 
              title="White" 
              value={`${results.inkUsage.white} cc`}
            />
          </Grid>
          <Grid item xs={4}>
            <SmallStat 
              title="CMYK" 
              value={`${results.inkUsage.cmyk} cc`}
            />
          </Grid>
          <Grid item xs={4}>
            <SmallStat 
              title="Primer" 
              value={`${results.inkUsage.primer} cc`}
            />
          </Grid>
        </Grid>

        <Typography variant="body2" sx={{ fontWeight: 500 }}>
          Ink Cost per Unit: {formatCurrency(results.inkCostPerUnit)}
        </Typography>
      </Paper>

      <Box sx={{ mt: 4, display: 'flex', justifyContent: 'flex-end' }}>
        <Button 
          variant="contained" 
          color="primary"
          size="large"
          sx={{ 
            px: 3,
            py: 1.5,
            fontWeight: 600,
            boxShadow: 2,
            '&:hover': {
              boxShadow: 4
            }
          }}
          disabled={true} // Will be enabled in Phase 2
        >
          DOWNLOAD PDF
        </Button>
      </Box>
    </Box>
  );
};

export default ResultsPanel;
