import React, { useState } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Slider, 
  Grid,
  InputAdornment,
  Card,
  CardContent,
  Tooltip,
  ButtonGroup,
  Button,
  Fade
} from '@mui/material';
import { useTheme } from '@mui/material/styles';
import PrinterInfoPopover from './PrinterInfoPopover';

// Icons for input sections using emojis (can be replaced with SVG icons)
const DimensionsIcon = () => <Box sx={{ fontSize: '1.5rem', color: 'primary.main' }}>üìè</Box>;
const PriceIcon = () => <Box sx={{ fontSize: '1.5rem', color: 'primary.main' }}>üí∞</Box>;
const VolumeIcon = () => <Box sx={{ fontSize: '1.5rem', color: 'primary.main' }}>üìä</Box>;
const InfoIcon = () => <Box sx={{ fontSize: '1.2rem', cursor: 'pointer' }}>‚ìò</Box>;

/**
 * Input Panel Component
 * Handles user inputs for the simulation with enhanced styling
 */
const InputPanel = ({ inputs, onInputChange }) => {
  const theme = useTheme();

  // State for printer info popover
  const [infoAnchorEl, setInfoAnchorEl] = useState(null);
  const infoPopoverOpen = Boolean(infoAnchorEl);

  // Handle info icon click
  const handleInfoClick = (event) => {
    setInfoAnchorEl(event.currentTarget);
  };

  // Handle info popover close
  const handleInfoClose = () => {
    setInfoAnchorEl(null);
  };

  // Handle numeric input changes with validation
  const handleNumericChange = (name, value, min, max) => {
    // Parse value as number
    const numValue = parseFloat(value);

    // Validate number is within range
    if (!isNaN(numValue) && (min === undefined || numValue >= min) && (max === undefined || numValue <= max)) {
      onInputChange(name, numValue);
    }
  };

  // Handle stepper button clicks
  const handleStepperClick = (name, step, min, max) => {
    const currentValue = inputs[name];
    const newValue = currentValue + step;

    // Validate the new value is within range
    if ((min === undefined || newValue >= min) && (max === undefined || newValue <= max)) {
      onInputChange(name, newValue);
    }
  };

  // Section header with icon
  const SectionHeader = ({ icon, title }) => (
    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
      <Box sx={{ mr: 1.5 }}>{icon}</Box>
      <Typography variant="h6" color="primary.main" fontWeight="600">
        {title}
      </Typography>
    </Box>
  );

  // Custom input field with label and steppers - removed small arrows
  const StepperInput = ({ 
    label, 
    value, 
    name, 
    endAdornment, 
    min, 
    max, 
    helperText, 
    startAdornment, 
    step = 1,
    fullWidth = true 
  }) => (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle2" gutterBottom sx={{ fontWeight: 500, color: 'text.secondary' }}>
        {label}
      </Typography>
      <Grid container spacing={1} alignItems="center">
        <Grid item xs={9}>
          <TextField
            value={value}
            onChange={(e) => handleNumericChange(name, e.target.value, min, max)}
            InputProps={{
              startAdornment: startAdornment && <InputAdornment position="start">{startAdornment}</InputAdornment>,
              endAdornment: endAdornment && <InputAdornment position="end">{endAdornment}</InputAdornment>,
              disableUnderline: true
            }}
            inputProps={{ 
              min, 
              max, 
              step: 1,
              style: { textAlign: 'center' } // Center text for better readability
            }}
            type="number"
            variant="outlined"
            size="medium"
            fullWidth
            sx={{
              '& .MuiOutlinedInput-root': {
                fontWeight: 500,
                transition: 'all 0.2s',
                '&:hover': {
                  borderColor: 'primary.main',
                },
                '&.Mui-focused': {
                  boxShadow: `0 0 0 3px rgba(0, 88, 163, 0.1)`,
                },
              },
              // Remove inner arrows from number inputs
              '& input[type=number]::-webkit-inner-spin-button, & input[type=number]::-webkit-outer-spin-button': {
                '-webkit-appearance': 'none',
                margin: 0
              },
              '& input[type=number]': {
                '-moz-appearance': 'textfield'
              }
            }}
          />
        </Grid>
        <Grid item xs={3}>
          <ButtonGroup
            orientation="vertical"
            size="small"
            variant="outlined"
            fullWidth
            sx={{
              '& .MuiButton-root': {
                minWidth: 'unset',
                py: 0.4,
                fontSize: '1.2rem',
                fontWeight: 'bold'
              }
            }}
          >
            <Button 
              onClick={() => handleStepperClick(name, step, min, max)}
              sx={{ borderBottomWidth: '1px !important' }}
            >
              +
            </Button>
            <Button 
              onClick={() => handleStepperClick(name, -step, min, max)}
            >
              ‚àí
            </Button>
          </ButtonGroup>
        </Grid>
      </Grid>
      {helperText && (
        <Typography variant="caption" color="text.secondary">
          {helperText}
        </Typography>
      )}
    </Box>
  );

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h5" gutterBottom sx={{ fontWeight: 600 }}>
          Simulation Parameters
        </Typography>
        <Tooltip title="Printer Specifications">
          <Box 
            component="span" 
            onClick={handleInfoClick}
            sx={{ 
              cursor: 'pointer',
              color: 'primary.main',
              transition: 'all 0.2s',
              '&:hover': {
                color: 'primary.dark',
                transform: 'scale(1.1)',
              },
            }}
          >
            <InfoIcon />
          </Box>
        </Tooltip>
        <PrinterInfoPopover
          open={infoPopoverOpen}
          anchorEl={infoAnchorEl}
          handleClose={handleInfoClose}
        />
      </Box>

      {/* Product Dimensions */}
      <Fade in={true} timeout={500}>
        <Card sx={{ mb: 3, overflow: 'visible' }}>
          <CardContent>
            <SectionHeader icon={<DimensionsIcon />} title="Product Dimensions" />

            <Grid container spacing={3}>
              <Grid item xs={6}>
                <StepperInput
                  label="Short Edge"
                  value={inputs.shortEdge}
                  name="shortEdge"
                  endAdornment="mm"
                  min={10}
                  max={305}
                  step={5}
                  helperText="Range: 10-305mm"
                />
              </Grid>
              <Grid item xs={6}>
                <StepperInput
                  label="Long Edge"
                  value={inputs.longEdge}
                  name="longEdge"
                  endAdornment="mm"
                  min={10}
                  max={458}
                  step={5}
                  helperText="Range: 10-458mm"
                />
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Fade>

      {/* Price Inputs */}
      <Fade in={true} timeout={600}>
        <Card sx={{ mb: 3, overflow: 'visible' }}>
          <CardContent>
            <SectionHeader icon={<PriceIcon />} title="Price Parameters" />

            <Grid container spacing={2}>
              <Grid item xs={12}>
                <StepperInput
                  label="Sales Price per Unit"
                  value={inputs.salesPricePerUnit}
                  name="salesPricePerUnit"
                  startAdornment="JPY"
                  min={0}
                  step={50}
                />
              </Grid>
              <Grid item xs={12}>
                <StepperInput
                  label="Material Cost per Unit"
                  value={inputs.materialCostPerUnit}
                  name="materialCostPerUnit"
                  startAdornment="JPY"
                  min={0}
                  step={10}
                />
              </Grid>
              <Grid item xs={12}>
                <StepperInput
                  label="Labor Cost per Hour"
                  value={inputs.laborCostPerHour}
                  name="laborCostPerHour"
                  startAdornment="JPY"
                  min={0}
                  step={100}
                />
              </Grid>
              <Grid item xs={12}>
                <StepperInput
                  label="Ink Price"
                  value={inputs.inkPricePerCC}
                  name="inkPricePerCC"
                  startAdornment="JPY"
                  endAdornment="/cc"
                  min={0}
                  step={1}
                />
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </Fade>

      {/* Sales Volume Slider */}
      <Fade in={true} timeout={700}>
        <Card sx={{ mb: 3, overflow: 'visible' }}>
          <CardContent>
            <SectionHeader icon={<VolumeIcon />} title="Monthly Sales Volume" />

            <Box sx={{ px: 1 }}>
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={8}>
                  <Slider
                    value={inputs.monthlySalesVolume}
                    onChange={(_, value) => onInputChange('monthlySalesVolume', value)}
                    min={0}
                    max={1000}
                    step={10}
                    color="primary"
                    valueLabelDisplay="auto"
                    sx={{
                      height: 8,
                      '& .MuiSlider-track': {
                        border: 'none',
                      },
                      '& .MuiSlider-valueLabel': {
                        backgroundColor: 'primary.main',
                        borderRadius: '8px',
                        padding: '4px 8px',
                        fontWeight: 600,
                      },
                    }}
                  />
                </Grid>
                <Grid item xs={4}>
                  <TextField
                    value={inputs.monthlySalesVolume}
                    onChange={(e) => handleNumericChange('monthlySalesVolume', e.target.value, 0, 1000)}
                    inputProps={{ 
                      min: 0, 
                      max: 1000,
                      style: { textAlign: 'center' }
                    }}
                    type="number"
                    size="small"
                    fullWidth
                    variant="outlined"
                    sx={{
                      '& .MuiOutlinedInput-root': {
                        fontWeight: 500,
                      },
                      // Remove inner arrows from number inputs
                      '& input[type=number]::-webkit-inner-spin-button, & input[type=number]::-webkit-outer-spin-button': {
                        '-webkit-appearance': 'none',
                        margin: 0
                      },
                      '& input[type=number]': {
                        '-moz-appearance': 'textfield'
                      }
                    }}
                  />
                </Grid>
              </Grid>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1, fontWeight: 500 }}>
                Units per month (0-1000)
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </Fade>
    </Box>
  );
};

export default InputPanel;
