import React from 'react';
import {
  Popover,
  Typography,
  Box,
  Table,
  TableBody,
  TableRow,
  TableCell,
  IconButton,
  TextField,
  InputAdornment
} from '@mui/material';
import { useLanguage } from '../../../context/LanguageContext';
import { PRINTER_SPECIFICATIONS } from '../../../config/constants';
import { formatNumber } from '../../../utils/formatters';

// Close icon as inline SVG for better styling control
const CloseIcon = () => (
  <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M18 6L6 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M6 6L18 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
  </svg>
);

/**
 * PrinterInfoPopover Component
 * Displays printer specifications and allows editing initial investment
 */
const PrinterInfoPopover = ({
  open,
  anchorEl,
  handleClose,
  initialInvestment,
  onInitialInvestmentChange,
  error
}) => {
  const { t, language } = useLanguage();

  // Handle input change for initial investment
  const handleInvestmentChange = (event) => {
    const value = event.target.value.replace(/,/g, '');
    const numValue = parseFloat(value);

    if (value === '') {
      onInitialInvestmentChange('');
    } else if (!isNaN(numValue) && numValue > 0) {
      onInitialInvestmentChange(numValue);
    }
  };

  // Format value for display
  const displayValue = initialInvestment === '' 
    ? '' 
    : Number(initialInvestment).toLocaleString(language === 'ja' ? 'ja-JP' : 'en-US');

  return (
    <Popover
      open={open}
      anchorEl={anchorEl}
      onClose={handleClose}
      anchorOrigin={{
        vertical: 'bottom',
        horizontal: 'right',
      }}
      transformOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      PaperProps={{
        sx: {
          p: 3,
          width: { xs: 280, sm: 340 },
          borderRadius: 2,
          boxShadow: '0 8px 20px rgba(0, 0, 0, 0.15)'
        }
      }}
    >
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h6" fontWeight={600}>
          {t('printerSpecifications')}
        </Typography>
        <IconButton size="small" onClick={handleClose} sx={{ color: 'text.secondary' }}>
          <CloseIcon />
        </IconButton>
      </Box>

      <Table size="small">
        <TableBody>
          <TableRow>
            <TableCell component="th" scope="row" sx={{ fontWeight: 500, color: 'text.secondary', pl: 0 }}>
              {t('printerModel')}
            </TableCell>
            <TableCell align="right" sx={{ fontWeight: 600 }}>
              {PRINTER_SPECIFICATIONS.name}
            </TableCell>
          </TableRow>

          <TableRow>
            <TableCell component="th" scope="row" sx={{ fontWeight: 500, color: 'text.secondary', pl: 0 }}>
              {t('printSpeed')}
            </TableCell>
            <TableCell align="right" sx={{ fontWeight: 600 }}>
              {PRINTER_SPECIFICATIONS.printSpeed} {t('printsPerHour')}
            </TableCell>
          </TableRow>

          <TableRow>
            <TableCell component="th" scope="row" sx={{ fontWeight: 500, color: 'text.secondary', pl: 0 }}>
              {t('printableArea')}
            </TableCell>
            <TableCell align="right" sx={{ fontWeight: 600 }}>
              {formatNumber(PRINTER_SPECIFICATIONS.printableArea.width, 0)} × {formatNumber(PRINTER_SPECIFICATIONS.printableArea.height, 0)} mm
            </TableCell>
          </TableRow>

          <TableRow>
            <TableCell 
              component="th" 
              scope="row" 
              sx={{ 
                fontWeight: 500, 
                color: error ? 'error.main' : 'text.secondary', 
                pl: 0,
                verticalAlign: 'top',
                pt: 2 
              }}
            >
              {t('initialInvestment')}
            </TableCell>
            <TableCell align="right" sx={{ pt: 2 }}>
              <TextField
                value={displayValue}
                onChange={handleInvestmentChange}
                variant="outlined"
                size="small"
                InputProps={{
                  startAdornment: <InputAdornment position="start">{t('currency')}</InputAdornment>,
                }}
                inputProps={{
                  style: { textAlign: 'right' }
                }}
                fullWidth
                error={Boolean(error)}
                helperText={error}
                sx={{
                  '& .MuiOutlinedInput-root': {
                    borderRadius: 1.5,
                    fontSize: '0.9rem'
                  },
                  '& input::-webkit-inner-spin-button, & input::-webkit-outer-spin-button': {
                    WebkitAppearance: 'none',
                    margin: 0
                  },
                  '& input[type=number]': {
                    MozAppearance: 'textfield'
                  }
                }}
              />
            </TableCell>
          </TableRow>
        </TableBody>
      </Table>

      <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 2 }}>
        {t('printerInfoDisclaimer') || 'These specifications are used for simulation calculations.'}
      </Typography>
    </Popover>
  );
};

export default PrinterInfoPopover;
