import React from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Slider, 
  Grid,
  InputAdornment,
  Tooltip,
  Divider,
  Icon,
  Paper
} from '@mui/material';
import { useTheme } from '@mui/material/styles';

// Icons for input sections (can be replaced with actual Material-UI icons)
const DimensionsIcon = () => <Box sx={{ fontSize: '1.5rem', color: 'primary.main' }}>📏</Box>;
const PriceIcon = () => <Box sx={{ fontSize: '1.5rem', color: 'primary.main' }}>💹</Box>;
const VolumeIcon = () => <Box sx={{ fontSize: '1.5rem', color: 'primary.main' }}>📊</Box>;

/**
 * Input Panel Component
 * Handles user inputs for the simulation with enhanced styling
 */
const InputPanel = ({ inputs, onInputChange }) => {
  const theme = useTheme();

  // Handle numeric input changes with validation
  const handleNumericChange = (name, value, min, max) => {
    // Parse value as number
    const numValue = parseFloat(value);

    // Validate number is within range
    if (!isNaN(numValue) && (min === undefined || numValue >= min) && (max === undefined || numValue <= max)) {
      onInputChange(name, numValue);
    }
  };

  // Section header with icon
  const SectionHeader = ({ icon, title }) => (
    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
      <Box sx={{ mr: 1.5 }}>{icon}</Box>
      <Typography variant="h6" color="primary.main" fontWeight="600">
        {title}
      </Typography>
    </Box>
  );

  // Custom input field with label
  const LabeledInput = ({ label, value, name, endAdornment, min, max, helperText, startAdornment, fullWidth = true }) => (
    <Box sx={{ mb: 2 }}>
      <Typography variant="subtitle2" gutterBottom sx={{ fontWeight: 500, color: 'text.secondary' }}>
        {label}
      </Typography>
      <TextField
        value={value}
        onChange={(e) => handleNumericChange(name, e.target.value, min, max)}
        InputProps={{
          startAdornment: startAdornment && <InputAdornment position="start">{startAdornment}</InputAdornment>,
          endAdornment: endAdornment && <InputAdornment position="end">{endAdornment}</InputAdornment>,
        }}
        inputProps={{ min, max, step: 1 }}
        type="number"
        variant="outlined"
        size="medium"
        fullWidth={fullWidth}
        sx={{
          '& .MuiOutlinedInput-root': {
            fontWeight: 500,
            transition: 'all 0.2s',
            '&:hover': {
              borderColor: 'primary.main',
            },
            '&.Mui-focused': {
              boxShadow: '0 0 0 3px rgba(0, 88, 163, 0.1)',
            },
          },
        }}
        helperText={helperText}
      />
    </Box>
  );

  return (
    <Box>
      <Typography variant="h5" gutterBottom sx={{ fontWeight: 600, mb: 3 }}>
        Simulation Parameters
      </Typography>

      {/* Product Dimensions */}
      <Box sx={{ mb: 4 }}>
        <SectionHeader icon={<DimensionsIcon />} title="Product Dimensions" />

        <Grid container spacing={3}>
          <Grid item xs={6}>
            <LabeledInput
              label="Short Edge"
              value={inputs.shortEdge}
              name="shortEdge"
              endAdornment="mm"
              min={10}
              max={305}
              helperText="Range: 10-305mm"
            />
          </Grid>
          <Grid item xs={6}>
            <LabeledInput
              label="Long Edge"
              value={inputs.longEdge}
              name="longEdge"
              endAdornment="mm"
              min={10}
              max={458}
              helperText="Range: 10-458mm"
            />
          </Grid>
        </Grid>
      </Box>

      <Divider sx={{ my: 3 }} />

      {/* Price Inputs */}
      <Box sx={{ mb: 4 }}>
        <SectionHeader icon={<PriceIcon />} title="Price Parameters" />

        <Grid container spacing={2}>
          <Grid item xs={12}>
            <LabeledInput
              label="Sales Price per Unit"
              value={inputs.salesPricePerUnit}
              name="salesPricePerUnit"
              startAdornment="JPY"
              min={0}
            />
          </Grid>
          <Grid item xs={12}>
            <LabeledInput
              label="Material Cost per Unit"
              value={inputs.materialCostPerUnit}
              name="materialCostPerUnit"
              startAdornment="JPY"
              min={0}
            />
          </Grid>
          <Grid item xs={12}>
            <LabeledInput
              label="Labor Cost per Hour"
              value={inputs.laborCostPerHour}
              name="laborCostPerHour"
              startAdornment="JPY"
              min={0}
            />
          </Grid>
          <Grid item xs={12}>
            <LabeledInput
              label="Ink Price"
              value={inputs.inkPricePerCC}
              name="inkPricePerCC"
              startAdornment="JPY"
              endAdornment="/cc"
              min={0}
            />
          </Grid>
        </Grid>
      </Box>

      <Divider sx={{ my: 3 }} />

      {/* Sales Volume Slider */}
      <Box sx={{ mb: 4 }}>
        <SectionHeader icon={<VolumeIcon />} title="Monthly Sales Volume" />

        <Box sx={{ px: 1 }}>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={8}>
              <Slider
                value={inputs.monthlySalesVolume}
                onChange={(_, value) => onInputChange('monthlySalesVolume', value)}
                min={0}
                max={1000}
                step={10}
                color="primary"
                valueLabelDisplay="auto"
                sx={{
                  height: 8,
                  '& .MuiSlider-track': {
                    border: 'none',
                  },
                  '& .MuiSlider-valueLabel': {
                    backgroundColor: 'primary.main',
                    borderRadius: '8px',
                    padding: '4px 8px',
                    fontWeight: 600,
                  },
                }}
              />
            </Grid>
            <Grid item xs={4}>
              <TextField
                value={inputs.monthlySalesVolume}
                onChange={(e) => handleNumericChange('monthlySalesVolume', e.target.value, 0, 1000)}
                inputProps={{ min: 0, max: 1000 }}
                type="number"
                size="small"
                fullWidth
                variant="outlined"
                sx={{
                  '& .MuiOutlinedInput-root': {
                    fontWeight: 500,
                  },
                }}
              />
            </Grid>
          </Grid>
          <Typography variant="body2" color="text.secondary" sx={{ mt: 1, fontWeight: 500 }}>
            Units per month (0-1000)
          </Typography>
        </Box>
      </Box>

      {/* Additional information */}
      <Paper elevation={0} sx={{ bgcolor: 'grey.50', p: 2, borderRadius: 2, mt: 4 }}>
        <Typography variant="subtitle2" gutterBottom color="text.primary" sx={{ fontWeight: 600 }}>
          MO-180 Printer Specifications
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
          Initial investment cost: <strong>JPY 3,780,000</strong>
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
          Print speed: <strong>6 prints/hour</strong>
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Printable area: <strong>305mm x 458mm</strong>
        </Typography>
      </Paper>
    </Box>
  );
};

export default InputPanel;
